106→    // MARK: - Search & Filter Controls
107→
108→    private var searchAndFilterControls: some View {
109→        Group {
110→            // Search bar
111→            HStack(spacing: 8) {
112→                Image(systemName: "magnifyingglass")
113→                    .foregroundStyle(.vaultSecondaryText)
114→                TextField("Search files", text: $searchText)
115→                    .textFieldStyle(.plain)
116→                    .accessibilityIdentifier("vault_search_field")
117→                if !searchText.isEmpty {
118→                    Button {
119→                        searchText = ""
120→                    } label: {
121→                        Image(systemName: "xmark.circle.fill")
122→                            .foregroundStyle(.vaultSecondaryText)
123→                    }
124→                    .buttonStyle(.plain)
125→                }
126→            }
127→            .padding(10)
128→            .vaultGlassBackground(cornerRadius: 12)
129→
130→            // Filter & sort menu
131→            Menu {
132→                Section("Filter") {
133→                    ForEach(FileFilter.allCases) { filter in
134→                        Button {
135→                            fileFilter = filter
136→                        } label: {
137→                            if fileFilter == filter {
138→                                Label(filter.rawValue, systemImage: "checkmark")
139→                            } else {
140→                                Label(filter.rawValue, systemImage: filter.icon)
141→                            }
142→                        }
143→                    }
144→                }
145→                Section("Sort") {
146→                    ForEach(SortOrder.allCases, id: \.self) { order in
147→                        Button {
148→                            sortOrder = order
149→                        } label: {
150→                            if sortOrder == order {
151→                                Label(order.rawValue, systemImage: "checkmark")
152→                            } else {
153→                                Text(order.rawValue)
154→                            }
155→                        }
156→                    }
157→                }
158→            } label: {
159→                Image(systemName: fileFilter == .media ? "line.3.horizontal.decrease" : "line.3.horizontal.decrease.circle.fill")
160→                    .fontWeight(.medium)
161→                    .padding(10)
162→                    .vaultGlassBackground(cornerRadius: 12)
163→            }
164→            .accessibilityIdentifier("vault_filter_menu")
165→            .accessibilityLabel("Filter and sort")
166→
167→            // Select button (hidden for shared vaults)
168→            if !isSharedVault {
169→                Button {
170→                    withAnimation(.easeInOut(duration: 0.2)) {
171→                        isEditing = true
172→                    }
173→                } label: {
174→                    Image(systemName: "checkmark.circle")
175→                        .fontWeight(.medium)
176→                        .padding(10)
177→                        .vaultGlassBackground(cornerRadius: 12)
178→                }
179→                .accessibilityIdentifier("vault_select_button")
180→                .accessibilityLabel("Select files")
181→            }
182→        }
183→    }
