private var toggleSection: some View {
    Section {
        Toggle("Enable iCloud Backup", isOn: $isBackupEnabled)
    } footer: {
        Text("Encrypted vault data is backed up to your iCloud Drive daily. Only you can decrypt it with your pattern.")
    }
}

private var statusSection: some View {
    Section("Backup Status") {
        if isBackingUp {
            VStack(alignment: .leading, spacing: 8) {
                HStack(spacing: 10) {
                    if backupStage != .uploading {
                        ProgressView()
                    }
                    Text(backupStage?.rawValue ?? "Preparing...")
                        .foregroundStyle(.vaultSecondaryText)
                }
                if backupStage == .uploading {
                    HStack(spacing: 8) {
                        ProgressView(value: uploadProgress)
                            .tint(Color.accentColor)
                        Text("\(Int(uploadProgress * 100))%")
                            .font(.caption)
                            .foregroundStyle(.vaultSecondaryText)
                            .monospacedDigit()
                    }
                }
                HStack(spacing: 6) {
                    Image(systemName: "exclamationmark.triangle.fill")
                        .font(.caption2)
                        .foregroundStyle(.vaultHighlight)
                    Text("Keep the app open until backup completes")
                        .font(.caption)
                        .foregroundStyle(.vaultSecondaryText)
                }
            }

            Button("Cancel Backup", role: .destructive) {
                backupTask?.cancel()
                backupTask = nil
                isBackingUp = false
                backupStage = nil
                UIApplication.shared.isIdleTimerDisabled = false
            }
            .font(.subheadline)
        } else if let date = lastBackupDate {
            HStack {
                Text("Last backup")
                Spacer()
                Text(date, style: .relative)
                    .foregroundStyle(.vaultSecondaryText)
            }

            if let next = nextBackupDate {
                HStack {
                    Text("Next backup")
                    Spacer()
                    if Date() >= next {
                        Text("On next app open")
                            .foregroundStyle(.vaultSecondaryText)
                    } else {
                        Text(next, style: .relative)
                            .foregroundStyle(.vaultSecondaryText)
                    }
                }
            }
        } else {
            Text("No backup yet")
                .foregroundStyle(.vaultSecondaryText)
        }

        if let errorMessage {
            VStack(alignment: .leading, spacing: 4) {
                Label("Backup Failed", systemImage: "exclamationmark.triangle.fill")
                    .foregroundStyle(.red)
                    .font(.subheadline.weight(.medium))
                Text(errorMessage)
                    .foregroundStyle(.vaultSecondaryText)

        }
    }
}

