  5→
  6→@MainActor
  7→@Observable
  8→final class SubscriptionManager: NSObject {
  9→    static let shared = SubscriptionManager()
 10→
 11→    private static let logger = Logger(subsystem: "app.vaultaire.ios", category: "Subscription")
 12→
 13→    private(set) var isPremium = false
 14→    private(set) var isConfigured = false
 15→    private(set) var hasOfferings = false
 16→    private(set) var customerInfo: CustomerInfo?
 17→
 18→    static let entitlementID = "lifetime"
 19→    static let maxFreeVaults = 5
 20→    static let maxFreeSharedVaults = 10
 21→    static let maxFreeFilesPerVault = 100
 22→
 23→    private static let premiumOverrideKey = "premiumTestingOverride"
 24→
 25→    /// Whether the app is running in a sandbox environment (TestFlight or Xcode)
 26→    static var isSandbox: Bool {
 27→        Bundle.main.appStoreReceiptURL?.lastPathComponent == "sandboxReceipt"
 28→    }
 29→
 30→    /// Testing override for premium status (persisted in UserDefaults)
 31→    var hasPremiumOverride: Bool {
 32→        get { UserDefaults.standard.bool(forKey: Self.premiumOverrideKey) }
 33→        set {
 34→            UserDefaults.standard.set(newValue, forKey: Self.premiumOverrideKey)
 35→            isPremium = newValue
 36→            Self.isPremiumSnapshot = newValue
 37→            UserDefaults(suiteName: VaultCoreConstants.appGroupIdentifier)?
 38→                .set(newValue, forKey: VaultCoreConstants.isPremiumKey)
 39→        }
 40→    }
 41→
 42→    private override init() {
 43→        super.init()
 44→        // Apply testing override on launch
 45→        if UserDefaults.standard.bool(forKey: Self.premiumOverrideKey) {
 46→            isPremium = true
 47→            Self.isPremiumSnapshot = true
 48→        }
 49→    }
 50→
 51→    func configure(apiKey: String) {
 52→        Purchases.logLevel = .debug
 53→        Purchases.configure(withAPIKey: apiKey)
 54→        Purchases.shared.delegate = self
 55→        isConfigured = true
 56→
 57→        Task {
 58→            await checkSubscriptionStatus()
 59→            await checkOfferings()
 60→        }
 61→    }
 62→
 63→    func checkSubscriptionStatus() async {
 64→        do {
 65→            let info = try await Purchases.shared.customerInfo()
 66→            updateFromCustomerInfo(info)
 67→        } catch {
 68→            Self.logger.error("Failed to fetch customer info: \(error.localizedDescription, privacy: .public)")
 69→        }
 70→    }
 71→
 72→    private func checkOfferings() async {
 73→        do {
 74→            let offerings = try await Purchases.shared.offerings()
 75→            hasOfferings = offerings.current != nil
 76→        } catch {
 77→            hasOfferings = false
 78→            Self.logger.error("Failed to fetch offerings: \(error.localizedDescription, privacy: .public)")
 79→        }
 80→    }
 81→
 82→    func restorePurchases() async throws {
 83→        let info = try await Purchases.shared.restorePurchases()
 84→        updateFromCustomerInfo(info)
 85→    }
 86→
 87→    // MARK: - Limit Checks
 88→
 89→    func canCreateVault(currentCount: Int) -> Bool {
 90→        isPremium || currentCount < Self.maxFreeVaults
 91→    }
 92→
 93→    func canJoinSharedVault(currentCount: Int) -> Bool {
 94→        isPremium || currentCount < Self.maxFreeSharedVaults
 95→    }
 96→
 97→    func canAddFile(currentFileCount: Int) -> Bool {
 98→        isPremium || currentFileCount < Self.maxFreeFilesPerVault
 99→    }
100→
101→    func canCreateSharedVault() -> Bool {
102→        isPremium
103→    }
104→
105→    func canCreateDuressVault() -> Bool {
106→        isPremium
107→    }
108→
109→    func canSyncWithICloud() -> Bool {
110→        isPremium
111→    }
112→
113→    func canExpandStorage() -> Bool {
114→        isPremium
115→    }
116→
117→    /// Thread-safe snapshot of premium status for non-MainActor callers (e.g. VaultStorage).
118→    ///
119→    /// SAFETY: `nonisolated(unsafe)` is sound here because:
120→    /// - Bool load/store is atomic on ARM64 (aligned, single-word)
121→    /// - Single writer (`@MainActor` `updateFromCustomerInfo`), multiple readers
122→    /// - Stale read has no correctness impact (worst case: extra paywall shown once)
123→    nonisolated(unsafe) static var isPremiumSnapshot: Bool = false
124→
125→    // MARK: - Update
126→
127→    func updateFromCustomerInfo(_ info: CustomerInfo) {
128→        customerInfo = info
129→        // Check for the specific "lifetime" entitlement, or fall back to any active entitlement
130→        // (covers sandbox/test mode where entitlement-product mapping may not be configured)
131→        let rcPremium = info.entitlements[Self.entitlementID]?.isActive == true
132→            || !info.entitlements.active.isEmpty
133→        // Don't downgrade if testing override is active
134→        isPremium = rcPremium || UserDefaults.standard.bool(forKey: Self.premiumOverrideKey)
135→        Self.isPremiumSnapshot = isPremium
136→
137→        // Cache premium status in app group for share extension access
138→        UserDefaults(suiteName: VaultCoreConstants.appGroupIdentifier)?
139→            .set(isPremium, forKey: VaultCoreConstants.isPremiumKey)
140→
141→        Self.logger.debug("isPremium=\(self.isPremium), active entitlements=\(info.entitlements.active.keys.joined(separator: ", "), privacy: .public)")
142→    }
143→}
144→
145→// MARK: - PurchasesDelegate
146→
147→extension SubscriptionManager: PurchasesDelegate {
148→    nonisolated func purchases(_ purchases: Purchases, receivedUpdated customerInfo: CustomerInfo) {
149→        Task { @MainActor in
150→            updateFromCustomerInfo(customerInfo)
151→        }
152→    }
153→}
