 301→    private func performNuclearWipe() {
 302→        Task {
 303→            await DuressHandler.shared.performNuclearWipe()
 304→            await MainActor.run {
 305→                // Reset app state to trigger onboarding
 306→                appState.resetToOnboarding()
 307→                dismiss()
 308→            }
 309→        }
 310→    }
 311→    
 312→    #if DEBUG
 313→    private func performDebugFullReset() {
 314→        Task {
 315→            await debugFullReset()
 316→            await MainActor.run {
 317→                dismiss()
 318→            }
 319→        }
 320→    }
 321→    
 322→    /// Performs a complete reset of all app data - DEBUG ONLY
 323→    private func debugFullReset() async {
 324→        settingsLogger.info("Starting full debug reset")
 325→        
 326→        // 1. Clear vault files and storage
 327→        await clearVaultStorage()
 328→        
 329→        // 2. Clear recovery phrase mappings
 330→        clearRecoveryMappings()
 331→        
 332→        // 3. Clear all UserDefaults
 333→        clearUserDefaults()
 334→        
 335→        // 4. Clear Keychain entries
 336→        clearKeychain()
 337→        
 338→        // 5. Clear temporary files
 339→        clearTemporaryFiles()
 340→        
 341→        // 6. Reset app state
 342→        await MainActor.run {
 343→            appState.lockVault()
 344→            appState.resetToOnboarding()
 345→        }
 346→        
 347→        settingsLogger.info("Full debug reset complete")
 348→    }
 349→    
 350→    private func clearVaultStorage() async {
 351→        // Delete vault storage directory
 352→        let fileManager = FileManager.default
 353→        let documentsURL = fileManager.urls(for: .documentDirectory, in: .userDomainMask)[0]
 354→        let vaultURL = documentsURL.appendingPathComponent("vault_storage")
 355→        
 356→        try? fileManager.removeItem(at: vaultURL)
 357→        
 358→        settingsLogger.debug("Vault storage cleared")
 359→    }
 360→    
 361→    private func clearRecoveryMappings() {
 362→        UserDefaults.standard.removeObject(forKey: "recovery_mapping")
 363→        
 364→        settingsLogger.debug("Recovery mappings cleared")
 365→    }
 366→    
 367→    private func clearUserDefaults() {
 368→        let domain = Bundle.main.bundleIdentifier!
 369→        UserDefaults.standard.removePersistentDomain(forName: domain)
 370→        UserDefaults.standard.synchronize()
 371→        
 372→        settingsLogger.debug("UserDefaults cleared")
 373→    }
 374→    
 375→    private func clearKeychain() {
 376→        // Clear all keychain items for the app
 377→        let secItemClasses = [
 378→            kSecClassGenericPassword,
 379→            kSecClassInternetPassword,
 380→            kSecClassCertificate,
 381→            kSecClassKey,
 382→            kSecClassIdentity
 383→        ]
 384→        
 385→        for itemClass in secItemClasses {
 386→            let spec: [String: Any] = [kSecClass as String: itemClass]
 387→            SecItemDelete(spec as CFDictionary)
 388→        }
 389→        
 390→        settingsLogger.debug("Keychain cleared")
 391→    }
 392→    
 393→    private func clearTemporaryFiles() {
 394→        let fileManager = FileManager.default
 395→        let tempURL = fileManager.temporaryDirectory
 396→        
 397→        // Remove all temporary files
 398→        if let contents = try? fileManager.contentsOfDirectory(at: tempURL, includingPropertiesForKeys: nil) {
 399→            for fileURL in contents {
 400→                try? fileManager.removeItem(at: fileURL)
 401→            }
 402→        }
 403→        
 404→        settingsLogger.debug("Temporary files cleared")
 405→    }
 406→    #endif
 407→}
