 712→
 713→    private func checkiCloudAvailable() async -> Bool {
 714→        let status = await CloudKitSharingManager.shared.checkiCloudStatus()
 715→        // .temporarilyUnavailable means signed in but CloudKit still syncing — treat as available
 716→        return status == .available || status == .temporarilyUnavailable
 717→    }
 718→
 719→    private func onAppear() {
 720→        Task {
 721→            let available = await checkiCloudAvailable()
 722→            iCloudAvailable = available
 723→            if !available && isBackupEnabled {
 724→                isBackupEnabled = false
 725→            }
 726→            // Auto-backup if enabled and overdue
 727→            if isBackupEnabled && available && isBackupOverdue {
 728→                performBackup()
 729→            }
 730→        }
 731→    }
 732→
 733→    private func handleToggleChange(_ enabled: Bool) {
 734→        guard enabled else { return }
 735→
 736→        // Check iCloud via CloudKit account status before allowing enable
 737→        Task {
 738→            let available = await checkiCloudAvailable()
 739→            guard available else {
 740→                isBackupEnabled = false
 741→                iCloudAvailable = false
 742→                return
 743→            }
 744→            // First enable — run backup immediately
 745→            performBackup()
 746→        }
 747→    }
 748→
 749→    private func performBackup() {
 750→        guard let key = appState.currentVaultKey else {
 751→            errorMessage = "No vault key available"
 752→            return
 753→        }
 754→
 755→        isBackingUp = true
 756→        backupStage = nil
 757→        uploadProgress = 0
 758→        errorMessage = nil
 759→        UIApplication.shared.isIdleTimerDisabled = true
 760→
 761→        backupTask = Task {
 762→            defer {
 763→                Task { @MainActor in
 764→                    UIApplication.shared.isIdleTimerDisabled = false
 765→                }
 766→            }
 767→            do {
 768→                try await backupManager.performBackup(with: key, onProgress: { stage in
 769→                    Task { @MainActor in
 770→                        backupStage = stage
 771→                    }
 772→                }, onUploadProgress: { progress in
 773→                    Task { @MainActor in
 774→                        uploadProgress = progress
 775→                    }
 776→                })
 777→                await MainActor.run {
 778→                    lastBackupTimestamp = Date().timeIntervalSince1970
 779→                    isBackingUp = false
 780→                    backupStage = nil
 781→                }
 782→            } catch is CancellationError {
 783→                await MainActor.run {
 784→                    isBackingUp = false
 785→                    backupStage = nil
 786→                }
 787→            } catch iCloudError.notAvailable {
 788→                await MainActor.run {
 789→                    errorMessage = "iCloud is not available. Check that you're signed in to iCloud in Settings."
 790→                    isBackingUp = false
 791→                    backupStage = nil
 792→                }
 793→            } catch iCloudError.fileNotFound {
 794→                await MainActor.run {
 795→                    errorMessage = "No vault data found to back up. Add some files first."
 796→                    isBackingUp = false
 797→                    backupStage = nil
 798→                }
 799→            } catch {
 800→                await MainActor.run {
 801→                    errorMessage = "\(error.localizedDescription)\n\nError type: \(type(of: error))\nDetails: \(error)"
 802→                    isBackingUp = false
 803→                    backupStage = nil
 804→                }
 805→            }
 806→        }
 807→    }
 808→}
