 969→    private func checkForBackup() async {
 970→        isCheckingBackup = true
 971→        let result = await backupManager.checkForBackup()
 972→        await MainActor.run {
 973→            switch result {
 974→            case .found(let info):
 975→                backupInfo = info
 976→                noBackupFound = false
 977→            case .notFound:
 978→                noBackupFound = true
 979→            case .error(let message):
 980→                noBackupFound = true
 981→                errorMessage = message
 982→            }
 983→            isCheckingBackup = false
 984→        }
 985→    }
