 987→    private func performRestore(with pattern: [Int]) {
 988→        guard pattern.count >= 6 else {
 989→            patternState.reset()
 990→            return
 991→        }
 992→
 993→        isRestoring = true
 994→        errorMessage = nil
 995→        restoreStage = "Deriving encryption key..."
 996→
 997→        Task {
 998→            do {
 999→                let key = try await KeyDerivation.deriveKey(from: pattern, gridSize: patternState.gridSize)
1000→
1001→                await MainActor.run { restoreStage = "Downloading from iCloud..." }
1002→                try await backupManager.restoreBackup(with: key)
1003→
1004→                await MainActor.run {
1005→                    isRestoring = false
1006→                    restoreSuccess = true
1007→                }
1008→            } catch iCloudError.notAvailable {
1009→                await MainActor.run {
1010→                    errorMessage = "iCloud is not available. Check your connection and try again."
1011→                    isRestoring = false
1012→                    patternState.reset()
1013→                }
1014→            } catch iCloudError.downloadFailed {
1015→                await MainActor.run {
1016→                    errorMessage = "Decryption failed. This usually means the pattern doesn't match the one used to create the backup."
1017→                    isRestoring = false
1018→                    patternState.reset()
1019→                }
1020→            } catch {
1021→                await MainActor.run {
1022→                    errorMessage = "\(error.localizedDescription)\n\nDetails: \(error)"
1023→                    isRestoring = false
1024→                    patternState.reset()
1025→                }
1026→            }
1027→        }
1028→    }
1029→}
