 196→            Text("⚠️ EXTREMELY DESTRUCTIVE ⚠️\n\nWhen this pattern is entered, ALL OTHER VAULTS will be PERMANENTLY DESTROYED with no warning or confirmation.\n\nThis includes:\n• All files in other vaults\n• All recovery phrases for other vaults\n• No way to undo this action\n\nOnly use this if you understand you may lose important data under duress.\n\nAre you absolutely sure?")
 197→        }
 198→        .onChange(of: isDuressVault) { oldValue, newValue in
 199→            guard !isSharedVault, activeShareCount == 0 else {
 200→                isDuressVault = oldValue
 201→                return
 202→            }
 203→            if newValue != oldValue {
 204→                pendingDuressValue = newValue
 205→                if newValue {
 206→                    // Trying to enable - show confirmation
 207→                    showingDuressConfirmation = true
 208→                } else {
 209→                    // Trying to disable - allow without confirmation
 210→                    removeDuressVault()
 211→                }
 212→            }
 213→        }
 214→        .task {
 215→            loadVaultStatistics()
 216→        }
 217→        .onChange(of: showingShareVault) { _, isShowing in
 218→            if !isShowing { loadVaultStatistics() }
 219→        }
 220→        .premiumPaywall(isPresented: $showingPaywall)
 221→    }
 222→
 223→    private func setAsDuressVault() {
 224→        guard let key = appState.currentVaultKey else { return }
 225→        SentryManager.shared.addBreadcrumb(category: "settings.duressToggled")
 226→        Task {
 227→            do {
 228→                try await DuressHandler.shared.setAsDuressVault(key: key)
 229→            } catch {
 230→                SentryManager.shared.captureError(error)
 231→            }
 232→        }
 233→    }
 234→
 235→    private func removeDuressVault() {
 236→        SentryManager.shared.addBreadcrumb(category: "settings.duressToggled")
 237→        Task {
 238→            await DuressHandler.shared.clearDuressVault()
 239→        }
 240→    }
 241→
 242→    private func deleteVault() {
 243→        guard let key = appState.currentVaultKey else {
 244→            dismiss()
 245→            return
 246→        }
 247→
 248→        // Delete all files and the vault index
 249→        do {
 250→            let index = try VaultStorage.shared.loadIndex(with: key)
 251→            for file in index.files where !file.isDeleted {
 252→                try? VaultStorage.shared.deleteFile(id: file.fileId, with: key)
 253→            }
 254→
 255→            // Remove any active CloudKit shares
 256→            if let shares = index.activeShares {
 257→                for share in shares {
 258→                    Task {
 259→                        try? await CloudKitSharingManager.shared.deleteSharedVault(shareVaultId: share.id)
 260→                    }
 261→                }
 262→            }
 263→
 264→            try VaultStorage.shared.deleteVaultIndex(for: key)
 265→        } catch {
 266→            vaultSettingsLogger.error("Delete vault error: \(error.localizedDescription, privacy: .public)")
 267→        }
 268→
 269→        // Clean up recovery data and duress status
 270→        Task {
 271→            try? await RecoveryPhraseManager.shared.deleteRecoveryData(for: key)
 272→            if await DuressHandler.shared.isDuressKey(key) {
 273→                await DuressHandler.shared.clearDuressVault()
 274→            }
 275→        }
 276→
 277→        appState.lockVault()
 278→        dismiss()
 279→    }
 280→    
 281→    private func regenerateRecoveryPhrase() {
 282→        guard let key = appState.currentVaultKey else { return }
 283→        SentryManager.shared.addBreadcrumb(category: "settings.phraseRegenerated")
 284→        Task {
 285→            do {
 286→                _ = try await RecoveryPhraseManager.shared.regenerateRecoveryPhrase(for: key)
 287→                vaultSettingsLogger.debug("Recovery phrase regenerated")
 288→                // Show the new phrase
 289→                await MainActor.run {
 290→                    showingRegeneratedPhrase = true
 291→                }
 292→            } catch {
 293→                vaultSettingsLogger.error("Failed to regenerate phrase: \(error.localizedDescription, privacy: .public)")
 294→            }
 295→        }
 296→    }
 297→    
 298→    private func loadVaultStatistics() {
 299→        guard let key = appState.currentVaultKey else { return }
 300→        
 301→        Task {
 302→            do {
 303→                let files = try VaultStorage.shared.listFiles(with: key)
 304→                let totalSize = files.reduce(0) { $0 + Int64($1.size) }
 305→                
 306→                // Check if this is the duress vault
 307→                let isDuress = await DuressHandler.shared.isDuressKey(key)
 308→
 309→                // Load sharing info
 310→                let index = try VaultStorage.shared.loadIndex(with: key)
 311→                let shared = index.isSharedVault ?? false
 312→                let shareCount = index.activeShares?.count ?? 0
 313→
 314→                await MainActor.run {
 315→                    fileCount = files.count
 316→                    storageUsed = totalSize
 317→                    isDuressVault = isDuress
 318→                    isSharedVault = shared
 319→                    activeShareCount = shareCount
 320→                }
 321→            } catch {
 322→                vaultSettingsLogger.error("Failed to load vault statistics: \(error.localizedDescription, privacy: .public)")
 323→                await MainActor.run {
 324→                    fileCount = 0
 325→                    storageUsed = 0
 326→                }
 327→            }
 328→        }
 329→    }
 330→    
 331→    private static let byteCountFormatter: ByteCountFormatter = {
 332→        let formatter = ByteCountFormatter()
 333→        formatter.countStyle = .file
 334→        formatter.allowedUnits = [.useBytes, .useKB, .useMB, .useGB]
 335→        return formatter
 336→    }()
 337→
 338→    private func formatBytes(_ bytes: Int64) -> String {
 339→        Self.byteCountFormatter.string(fromByteCount: bytes)
 340→    }
 341→}
