 600→    // MARK: - Actions
 601→
 602→    private func handlePatternComplete(_ pattern: [Int]) {
 603→        vaultSettingsLogger.debug("Pattern completed in \(String(describing: step), privacy: .public) step")
 604→        
 605→        switch step {
 606→        case .verifyCurrent:
 607→            verifyCurrentPattern(pattern)
 608→            
 609→        case .createNew:
 610→            validateNewPattern(pattern)
 611→            
 612→        case .confirmNew:
 613→            confirmNewPattern(pattern)
 614→            
 615→        case .complete:
 616→            break
 617→        }
 618→    }
 619→    
 620→    private func verifyCurrentPattern(_ pattern: [Int]) {
 621→        guard let currentKey = appState.currentVaultKey else {
 622→            errorMessage = "No vault key available"
 623→            patternState.reset()
 624→            return
 625→        }
 626→
 627→        // Validate pattern structure first
 628→        let validation = PatternValidator.shared.validate(pattern, gridSize: patternState.gridSize)
 629→        if !validation.isValid {
 630→            errorMessage = validation.errors.first?.message ?? "Invalid pattern"
 631→            patternState.reset()
 632→            return
 633→        }
 634→
 635→        isProcessing = true
 636→
 637→        Task {
 638→            do {
 639→                // Derive key from entered pattern and check if it matches current key
 640→                let enteredKey = try await KeyDerivation.deriveKey(from: pattern, gridSize: patternState.gridSize)
 641→                
 642→                await MainActor.run {
 643→                    if enteredKey == currentKey {
 644→                        // Pattern verified - move to next step
 645→                        vaultSettingsLogger.debug("Current pattern verified")
 646→                        currentPattern = pattern
 647→                        step = .createNew
 648→                        patternState.reset()
 649→                        errorMessage = nil
 650→                    } else {
 651→                        // Pattern doesn't match
 652→                        vaultSettingsLogger.debug("Current pattern incorrect")
 653→                        errorMessage = "Incorrect pattern. Please try again."
 654→                        patternState.reset()
 655→                    }
 656→                    isProcessing = false
 657→                }
 658→            } catch {
 659→                await MainActor.run {
 660→                    errorMessage = "Error verifying pattern: \(error.localizedDescription)"
 661→                    patternState.reset()
 662→                    isProcessing = false
 663→                }
 664→            }
 665→        }
 666→    }
 667→    
 668→    private func validateNewPattern(_ pattern: [Int]) {
 669→        isProcessing = true
 670→        
 671→        Task {
 672→            // First, validate the pattern structure
 673→            let result = PatternValidator.shared.validate(pattern, gridSize: patternState.gridSize)
 674→
 675→            await MainActor.run {
 676→                validationResult = result
 677→            }
 678→
 679→            if result.isValid {
 680→                // Check if this pattern already exists as another vault
 681→                do {
 682→                    let newKey = try await KeyDerivation.deriveKey(from: pattern, gridSize: patternState.gridSize)
 683→                    
 684→                    // Check if a vault with this key already exists
 685→                    let vaultExists = VaultStorage.shared.vaultExists(for: newKey)
 686→                    
 687→                    await MainActor.run {
 688→                        if vaultExists {
 689→                            vaultSettingsLogger.info("Pattern already used by another vault")
 690→                            errorMessage = "This pattern is already used by another vault. Please choose a different pattern."
 691→                            patternState.reset()
 692→                        } else {
 693→                            vaultSettingsLogger.debug("New pattern valid and unique")
 694→                            newPattern = pattern
 695→                            step = .confirmNew
 696→                            patternState.reset()
 697→                            errorMessage = nil
 698→                        }
 699→                        isProcessing = false
 700→                    }
 701→                } catch {
 702→                    await MainActor.run {
 703→                        errorMessage = "Error checking pattern: \(error.localizedDescription)"
 704→                        patternState.reset()
 705→                        isProcessing = false
 706→                    }
 707→                }
 708→            } else {
 709→                vaultSettingsLogger.debug("New pattern invalid")
 710→                await MainActor.run {
 711→                    patternState.reset()
 712→                    isProcessing = false
 713→                }
 714→            }
 715→        }
 716→    }
 717→    
 718→    private func confirmNewPattern(_ pattern: [Int]) {
 719→        let validation = PatternValidator.shared.validate(pattern, gridSize: patternState.gridSize)
 720→        if !validation.isValid {
 721→            errorMessage = validation.errors.first?.message ?? "Invalid pattern"
 722→            patternState.reset()
 723→            return
 724→        }
 725→
 726→        if pattern == newPattern {
 727→            vaultSettingsLogger.debug("Patterns match, updating vault")
 728→            updateVaultPattern(pattern)
 729→        } else {
 730→            vaultSettingsLogger.debug("Patterns don't match")
 731→            errorMessage = "Patterns don't match. Please try again."
 732→            patternState.reset()
 733→        }
 734→    }
 735→    
 736→    private func updateVaultPattern(_ newPattern: [Int]) {
 737→        guard let oldKey = appState.currentVaultKey else {
 738→            errorMessage = "No vault key available"
 739→            return
 740→        }
 741→        
 742→        isProcessing = true
 743→        
 744→        Task {
 745→            do {
 746→                // 1. Derive new key from new pattern
 747→                let newKey = try await KeyDerivation.deriveKey(from: newPattern, gridSize: patternState.gridSize)
 748→                
 749→                vaultSettingsLogger.trace("New key derived")
 750→                
 751→                // 2. Change vault key (instant operation - only re-encrypts master key)
 752→                try VaultStorage.shared.changeVaultKey(from: oldKey, to: newKey)
 753→                
 754→                vaultSettingsLogger.debug("Vault key changed")
 755→                
 756→                // 3. Generate a new recovery phrase
 757→                let recoveryPhrase = RecoveryPhraseGenerator.shared.generatePhrase()
 758→                
 759→                vaultSettingsLogger.trace("New recovery phrase generated")
 760→                
 761→                // 4. Save the new recovery phrase with the new key
 762→                try await RecoveryPhraseManager.shared.saveRecoveryPhrase(
 763→                    phrase: recoveryPhrase,
 764→                    pattern: newPattern,
 765→                    gridSize: patternState.gridSize,
 766→                    patternKey: newKey
 767→                )
 768→                
 769→                vaultSettingsLogger.debug("Recovery phrase saved with new key")
 770→                
 771→                // 5. Delete old recovery data
 772→                try await RecoveryPhraseManager.shared.deleteRecoveryData(for: oldKey)
 773→                
 774→                vaultSettingsLogger.debug("Old recovery data deleted")
 775→                
 776→                SentryManager.shared.addBreadcrumb(category: "settings.patternChanged")
 777→
 778→                // 6. Update app state with new key
 779→                await MainActor.run {
 780→                    appState.currentVaultKey = newKey
 781→                    newRecoveryPhrase = recoveryPhrase
 782→                    step = .complete
 783→                    isProcessing = false
 784→                    errorMessage = nil
 785→
 786→                    vaultSettingsLogger.info("Pattern change complete")
 787→                }
 788→            } catch {
 789→                vaultSettingsLogger.error("Error updating pattern: \(error.localizedDescription, privacy: .public)")
 790→                await MainActor.run {
 791→                    errorMessage = "Failed to update pattern: \(error.localizedDescription)"
 792→                    patternState.reset()
 793→                    isProcessing = false
 794→                }
 795→            }
 796→        }
 797→    }
 798→}
