1208→    /// Generates thumbnail + duration from a video URL.
1209→    private static func generateVideoMetadata(from url: URL) async -> (thumbnail: Data?, duration: TimeInterval?) {
1210→        let asset = AVAsset(url: url)
1211→        let generator = AVAssetImageGenerator(asset: asset)
1212→        generator.appliesPreferredTrackTransform = true
1213→        generator.maximumSize = CGSize(width: 400, height: 400)
1214→
1215→        var thumbnail: Data?
1216→        var duration: TimeInterval?
1217→
1218→        if let cmDuration = try? await asset.load(.duration) {
1219→            let seconds = CMTimeGetSeconds(cmDuration)
1220→            if seconds.isFinite && seconds > 0 { duration = seconds }
1221→        }
1222→
1223→        let time = CMTime(seconds: 0.5, preferredTimescale: 600)
1224→        if let cgImage = try? generator.copyCGImage(at: time, actualTime: nil) {
1225→            thumbnail = UIImage(cgImage: cgImage).jpegData(compressionQuality: 0.7)
1226→        }
1227→
1228→        return (thumbnail, duration)
1229→    }
