import XCTest
@testable import Vault

final class OnboardingStepTests: XCTestCase {

    // MARK: - Case Count

    func testAllCasesCountIsFive() {
        XCTAssertEqual(OnboardingStep.allCases.count, 5)
    }

    // MARK: - Ordering

    func testCaseOrderMatchesExpectedFlow() {
        let expected: [OnboardingStep] = [.welcome, .permissions, .analytics, .paywall, .thankYou]
        XCTAssertEqual(OnboardingStep.allCases, expected)
    }

    // MARK: - next()

    func testNextFromWelcomeReturnsPermissions() {
        XCTAssertEqual(OnboardingStep.welcome.next(), .permissions)
    }

    func testNextFromPermissionsReturnsAnalytics() {
        XCTAssertEqual(OnboardingStep.permissions.next(), .analytics)
    }

    func testNextFromAnalyticsReturnsPaywall() {
        XCTAssertEqual(OnboardingStep.analytics.next(), .paywall)
    }

    func testNextFromPaywallReturnsThankYou() {
        XCTAssertEqual(OnboardingStep.paywall.next(), .thankYou)
    }

    func testNextFromThankYouReturnsNil() {
        XCTAssertNil(OnboardingStep.thankYou.next())
    }

    // MARK: - previous()

    func testPreviousFromWelcomeReturnsNil() {
        XCTAssertNil(OnboardingStep.welcome.previous())
    }

    func testPreviousFromPermissionsReturnsWelcome() {
        XCTAssertEqual(OnboardingStep.permissions.previous(), .welcome)
    }

    func testPreviousFromThankYouReturnsPaywall() {
        XCTAssertEqual(OnboardingStep.thankYou.previous(), .paywall)
    }

    // MARK: - progressFraction

    func testProgressFractionForFirstStep() {
        let fraction = OnboardingStep.welcome.progressFraction
        XCTAssertEqual(fraction, 1.0 / 5.0, accuracy: 0.001)
    }

    func testProgressFractionForLastStep() {
        let fraction = OnboardingStep.thankYou.progressFraction
        XCTAssertEqual(fraction, 1.0, accuracy: 0.001)
    }

    func testProgressFractionIncreases() {
        let fractions = OnboardingStep.allCases.map(\.progressFraction)
        for i in 1..<fractions.count {
            XCTAssertGreaterThan(fractions[i], fractions[i - 1],
                                 "Fraction at index \(i) should be greater than \(i - 1)")
        }
    }
}

import XCTest
@testable import Vault

final class SVDFSerializerTests: XCTestCase {

    private let shareKey = CryptoEngine.generateRandomBytes(count: 32)!

    private func makeFile(
        id: UUID = UUID(),
        name: String = "test.jpg",
        size: Int = 100,
        content: Data? = nil
    ) -> SharedVaultData.SharedFile {
        SharedVaultData.SharedFile(
            id: id,
            filename: name,
            mimeType: "image/jpeg",
            size: size,
            encryptedContent: content ?? Data("encrypted-\(name)".utf8),
            createdAt: Date(timeIntervalSince1970: 1700000000),
            encryptedThumbnail: Data("thumb".utf8)
        )
    }

    private func makeMetadata() -> SharedVaultData.SharedVaultMetadata {
        SharedVaultData.SharedVaultMetadata(
            ownerFingerprint: "abc123",
            sharedAt: Date(timeIntervalSince1970: 1700000000)
        )
    }

    // MARK: - Build Full

    func testBuildFullAndParseHeader() throws {
        let files = [makeFile(name: "a.jpg"), makeFile(name: "b.jpg")]
        let (data, _) = try SVDFSerializer.buildFull(
            files: files, metadata: makeMetadata(), shareKey: shareKey
        )

        let header = try SVDFSerializer.parseHeader(from: data)
        XCTAssertEqual(header.version, 4)
        XCTAssertEqual(header.fileCount, 2)
        XCTAssertTrue(header.manifestOffset > 0)
        XCTAssertTrue(header.manifestSize > 0)
        XCTAssertTrue(header.metadataOffset > header.manifestOffset)
    }

    func testBuildFullManifest() throws {
        let id1 = UUID()
        let id2 = UUID()
        let files = [makeFile(id: id1, name: "a.jpg"), makeFile(id: id2, name: "b.jpg")]
        let (data, _) = try SVDFSerializer.buildFull(
            files: files, metadata: makeMetadata(), shareKey: shareKey
        )

        let manifest = try SVDFSerializer.parseManifest(from: data, shareKey: shareKey)
        XCTAssertEqual(manifest.count, 2)
        XCTAssertEqual(manifest[0].id, id1.uuidString)
        XCTAssertEqual(manifest[1].id, id2.uuidString)
        XCTAssertFalse(manifest[0].deleted)
    }

    // MARK: - Magic Bytes

    func testIsSVDF() {
        let validMagic = Data([0x53, 0x56, 0x44, 0x34]) + Data(repeating: 0, count: 60)
        XCTAssertTrue(SVDFSerializer.isSVDF(validMagic))

        let invalidMagic = Data([0x00, 0x00, 0x00, 0x00])
        XCTAssertFalse(SVDFSerializer.isSVDF(invalidMagic))

        let tooShort = Data([0x53, 0x56])
        XCTAssertFalse(SVDFSerializer.isSVDF(tooShort))
    }

    // MARK: - Extract File Entry

    func testExtractFileEntry() throws {
        let id = UUID()
        let files = [makeFile(id: id, name: "extract-me.jpg")]
        let (data, manifest) = try SVDFSerializer.buildFull(
            files: files, metadata: makeMetadata(), shareKey: shareKey
        )

        let entry = manifest[0]
        let file = try SVDFSerializer.extractFileEntry(from: data, at: entry.offset, size: entry.size)
        XCTAssertEqual(file.id, id)
        XCTAssertEqual(file.filename, "extract-me.jpg")
        XCTAssertEqual(file.mimeType, "image/jpeg")
    }

    // MARK: - Incremental Append

    func testBuildIncrementalAppend() throws {
        let files = [makeFile(name: "a.jpg"), makeFile(name: "b.jpg")]
        let (priorData, priorManifest) = try SVDFSerializer.buildFull(
            files: files, metadata: makeMetadata(), shareKey: shareKey
        )

        let newFile = makeFile(name: "c.jpg")
        let (updatedData, updatedManifest) = try SVDFSerializer.buildIncremental(
            priorData: priorData,
            priorManifest: priorManifest,
            newFiles: [newFile],
            removedFileIds: [],
            metadata: makeMetadata(),
            shareKey: shareKey
        )

        XCTAssertEqual(updatedManifest.count, 3)
        let header = try SVDFSerializer.parseHeader(from: updatedData)
        XCTAssertEqual(header.fileCount, 3)
    }

    // MARK: - Incremental Delete

    func testBuildIncrementalDelete() throws {
        let id1 = UUID()
        let id2 = UUID()
        let files = [makeFile(id: id1, name: "a.jpg"), makeFile(id: id2, name: "b.jpg")]
        let (priorData, priorManifest) = try SVDFSerializer.buildFull(
            files: files, metadata: makeMetadata(), shareKey: shareKey
        )

        let (updatedData, updatedManifest) = try SVDFSerializer.buildIncremental(
            priorData: priorData,
            priorManifest: priorManifest,
            newFiles: [],
            removedFileIds: [id1.uuidString],
            metadata: makeMetadata(),
            shareKey: shareKey
        )

        XCTAssertTrue(updatedManifest[0].deleted)
        XCTAssertFalse(updatedManifest[1].deleted)
        let header = try SVDFSerializer.parseHeader(from: updatedData)
        XCTAssertEqual(header.fileCount, 1) // active count
    }

    // MARK: - Header Field Offsets

    func testHeaderFieldOffsets() throws {
        let files = [makeFile()]
        let (data, _) = try SVDFSerializer.buildFull(
            files: files, metadata: makeMetadata(), shareKey: shareKey
        )

        // Verify magic at bytes 0-3
        XCTAssertEqual(data[0], 0x53) // S
        XCTAssertEqual(data[1], 0x56) // V
        XCTAssertEqual(data[2], 0x44) // D
        XCTAssertEqual(data[3], 0x34) // 4

        let header = try SVDFSerializer.parseHeader(from: data)
        XCTAssertEqual(header.version, SVDFSerializer.currentVersion)
        XCTAssertTrue(header.manifestOffset >= UInt64(SVDFSerializer.headerSize))
        XCTAssertTrue(header.metadataOffset > header.manifestOffset)
    }

    // MARK: - Empty Files

    func testEmptyFilesArray() throws {
        let (data, manifest) = try SVDFSerializer.buildFull(
            files: [], metadata: makeMetadata(), shareKey: shareKey
        )

        XCTAssertTrue(SVDFSerializer.isSVDF(data))
        XCTAssertEqual(manifest.count, 0)
        let header = try SVDFSerializer.parseHeader(from: data)
        XCTAssertEqual(header.fileCount, 0)
    }

    func testBuildFullThrowsForOversizedFileSizeField() throws {
        let oversized = makeFile(
            name: "big.bin",
            size: Int(UInt32.max) + 1,
            content: Data(repeating: 0xAB, count: 16)
        )

        XCTAssertThrowsError(
            try SVDFSerializer.buildFull(
                files: [oversized],
                metadata: makeMetadata(),
                shareKey: shareKey
            )
        )
    }

    func testBuildFullThrowsForOversizedFilenameField() throws {
        let longName = String(repeating: "a", count: Int(UInt16.max) + 1)
        let file = makeFile(name: longName, content: Data(repeating: 0xCD, count: 16))

        XCTAssertThrowsError(
            try SVDFSerializer.buildFull(
                files: [file],
                metadata: makeMetadata(),
                shareKey: shareKey
            )
        )
    }

    func testBuildFullStreamingFromPlaintextRoundTrip() throws {
        let tempDir = FileManager.default.temporaryDirectory
            .appendingPathComponent(UUID().uuidString, isDirectory: true)
        try FileManager.default.createDirectory(at: tempDir, withIntermediateDirectories: true)
        defer { try? FileManager.default.removeItem(at: tempDir) }

        let plaintextURL = tempDir.appendingPathComponent("source.bin")
        let sourceData = Data(repeating: 0x42, count: 256 * 1024)
        try sourceData.write(to: plaintextURL)

        let svdfURL = tempDir.appendingPathComponent("vault.svdf")
        let metadata = makeMetadata()
        let createdAt = Date(timeIntervalSince1970: 1_700_000_000)
        let source = SVDFSerializer.StreamingSourceFile(
            id: UUID(),
            filename: "source.bin",
            mimeType: "application/octet-stream",
            originalSize: sourceData.count,
            createdAt: createdAt,
            encryptedThumbnail: nil,
            plaintextContentURL: plaintextURL,
            duration: nil
        )

        let (_, fileIds) = try SVDFSerializer.buildFullStreamingFromPlaintext(
            to: svdfURL,
            fileCount: 1,
            forEachFile: { _ in source },
            metadata: metadata,
            shareKey: shareKey
        )

        let svdfData = try Data(contentsOf: svdfURL)
        let decoded = try SVDFSerializer.deserialize(from: svdfData, shareKey: shareKey)

        XCTAssertEqual(fileIds.count, 1)
        XCTAssertEqual(decoded.files.count, 1)
        XCTAssertEqual(decoded.files[0].filename, "source.bin")
        XCTAssertEqual(decoded.files[0].size, sourceData.count)
    }

    func testBuildFullStreamingFromPlaintextLargeFileDecryptsWithDecryptStaged() throws {
        let tempDir = FileManager.default.temporaryDirectory
            .appendingPathComponent(UUID().uuidString, isDirectory: true)
        try FileManager.default.createDirectory(at: tempDir, withIntermediateDirectories: true)
        defer { try? FileManager.default.removeItem(at: tempDir) }

        let plaintextURL = tempDir.appendingPathComponent("large-source.bin")
        let sourceData = Data(repeating: 0x6A, count: (2 * 1024 * 1024) + 123)
        try sourceData.write(to: plaintextURL)

        let svdfURL = tempDir.appendingPathComponent("large-vault.svdf")
        let metadata = makeMetadata()
        let createdAt = Date(timeIntervalSince1970: 1_700_000_100)
        let source = SVDFSerializer.StreamingSourceFile(
            id: UUID(),
            filename: "large-source.bin",
            mimeType: "application/octet-stream",
            originalSize: sourceData.count,
            createdAt: createdAt,
            encryptedThumbnail: nil,
            plaintextContentURL: plaintextURL,
            duration: nil
        )

        _ = try SVDFSerializer.buildFullStreamingFromPlaintext(
            to: svdfURL,
            fileCount: 1,
            forEachFile: { _ in source },
            metadata: metadata,
            shareKey: shareKey
        )

        let svdfData = try Data(contentsOf: svdfURL)
        let decoded = try SVDFSerializer.deserialize(from: svdfData, shareKey: shareKey)
        XCTAssertEqual(decoded.files.count, 1)

        // Large shared payloads are VCSE streaming-encrypted and must be
        // decrypted through staged/streaming-aware path.
        let decrypted = try CryptoEngine.decryptStaged(decoded.files[0].encryptedContent, with: shareKey)
        XCTAssertEqual(decrypted.count, sourceData.count)
        XCTAssertEqual(decrypted, sourceData)
    }
}

