    // MARK: - Thread-Safe Queue

    private actor Queue<T: Sendable> {
        private var items: [T]
        private var idx = 0

        init(_ items: [T]) { self.items = items }

        func next() -> T? {
            guard idx < items.count else { return nil }
            let item = items[idx]
            idx += 1
            return item
        }
    }

