        let videoQueue = Queue(videoWork)
        let imageQueue = Queue(imageWork)

        await withTaskGroup(of: Void.self) { group in
            for _ in 0..<videoWorkerCount {
                group.addTask {
                    while let item = await videoQueue.next() {
                        guard !Task.isCancelled else { return }
                        do {
