  1→import SwiftUI
  2→import StoreKit
  3→
  4→struct VaultairePaywallView: View {
  5→    let onDismiss: () -> Void
  6→
  7→    @Environment(SubscriptionManager.self) private var subscriptionManager
  8→
  9→    @State private var selectedPlan: PlanType = .monthly
 10→    @State private var monthlyProduct: Product?
 11→    @State private var annualProduct: Product?
 12→    @State private var lifetimeProduct: Product?
 13→    @State private var isTrialEligible = false
 14→    @State private var trialEnabled = false
 15→    @State private var isPurchasing = false
 16→    @State private var errorMessage: String?
 17→
 18→    enum PlanType: String, CaseIterable {
 19→        case monthly, annual, lifetime
 20→    }
 21→
 22→    var body: some View {
 23→        ScrollView {
 24→            VStack(spacing: 20) {
 25→                headerSection
 26→                benefitsTable
 27→                planSelector
 28→                trialToggle
 29→                ctaButton
 30→                footerSection
 31→            }
 32→            .padding(.horizontal, 20)
 33→            .padding(.top, 16)
 34→            .padding(.bottom, 32)
 35→        }
 36→        .background(Color.vaultBackground.ignoresSafeArea())
 37→        .task {
 38→            await loadProducts()
 39→        }
 40→    }
 41→
 42→    // MARK: - Header
 43→
 44→    private var headerSection: some View {
 45→        VStack(spacing: 8) {
 46→            Text("Unlock Vaultaire Pro")
 47→                .font(.title.bold())
 48→                .foregroundStyle(.vaultText)
 49→
 50→            Text("Full privacy, your terms.")
 51→                .font(.subheadline)
 52→                .foregroundStyle(.vaultSecondaryText)
 53→        }
 54→    }
 55→
 56→    // MARK: - Benefits Table
 57→
 58→    private var benefitsTable: some View {
 59→        VStack(spacing: 0) {
 60→            HStack {
 61→                Text("Feature")
 62→                    .font(.caption.weight(.semibold))
 63→                    .foregroundStyle(.vaultSecondaryText)
