  1→import Foundation
  2→import StoreKit
  3→import os.log
  4→
  5→@MainActor
  6→@Observable
  7→final class SubscriptionManager {
  8→    static let shared = SubscriptionManager()
  9→
 10→    private static let logger = Logger(subsystem: "app.vaultaire.ios", category: "Subscription")
 11→
 12→    private(set) var isPremium = false
 13→    private(set) var products: [Product] = []
 14→    private(set) var purchasedProductIDs: Set<String> = []
 15→
 16→    static let productIDs = [
 17→        "monthly_pro",
 18→        "yearly_pro",
 19→        "lifetime",
 20→    ]
 21→
 22→    static let subscriptionGroupID = "vaultaire_pro"
 23→
 24→    static let maxFreeVaults = 5
 25→    static let maxFreeSharedVaults = 10
 26→    static let maxFreeFilesPerVault = 100
 27→
 28→    private static let premiumOverrideKey = "premiumTestingOverride"
 29→
 30→    /// Whether the app is running in a sandbox environment (TestFlight or Xcode)
 31→    static var isSandbox: Bool {
 32→        Bundle.main.appStoreReceiptURL?.lastPathComponent == "sandboxReceipt"
 33→    }
 34→
 35→    /// Testing override for premium status (persisted in UserDefaults)
 36→    var hasPremiumOverride: Bool {
 37→        get { UserDefaults.standard.bool(forKey: Self.premiumOverrideKey) }
 38→        set {
 39→            UserDefaults.standard.set(newValue, forKey: Self.premiumOverrideKey)
 40→            isPremium = newValue
 41→            Self.isPremiumSnapshot = newValue
 42→            UserDefaults(suiteName: VaultCoreConstants.appGroupIdentifier)?
 43→                .set(newValue, forKey: VaultCoreConstants.isPremiumKey)
 44→        }
 45→    }
 46→
 47→    private var transactionListener: Task<Void, Never>?
 48→
 49→    private init() {
 50→        // Start listening for transactions immediately
 51→        transactionListener = listenForTransactions()
 52→
 53→        // Apply testing override on launch
 54→        if UserDefaults.standard.bool(forKey: Self.premiumOverrideKey) {
 55→            isPremium = true
 56→            Self.isPremiumSnapshot = true
 57→        }
 58→
 59→        Task {
 60→            await loadProducts()
 61→            await updatePurchasedProducts()
 62→        }
 63→    }
 64→
 65→    nonisolated deinit {
 66→        // Task is automatically cancelled when reference is dropped
 67→    }
 68→
 69→    // MARK: - Products
 70→
 71→    func loadProducts() async {
 72→        do {
 73→            let loaded = try await Product.products(for: Self.productIDs)
 74→            products = loaded.sorted { a, b in
 75→                Self.productIDs.firstIndex(of: a.id) ?? 0 < Self.productIDs.firstIndex(of: b.id) ?? 0
 76→            }
 77→        } catch {
 78→            Self.logger.error("Failed to load products: \(error.localizedDescription, privacy: .public)")
 79→        }
 80→    }
 81→
 82→    var hasProducts: Bool { !products.isEmpty }
