232→        }
233→        .onChange(of: isDuressVault) { oldValue, newValue in
234→            let duressDisabledBySharing = Self.shouldDisableDuressForSharing(
235→                isSharedVault: isSharedVault,
236→                activeShareCount: activeShareCount,
237→                activeUploadCount: activeUploadCount
238→            )
239→            guard !duressDisabledBySharing else {
