242→            }
243→            if newValue != oldValue {
244→                pendingDuressValue = newValue
245→                if newValue {
246→                    // Trying to enable - show confirmation
247→                    showingDuressConfirmation = true
248→                } else {
249→                    // Trying to disable - allow without confirmation
250→                    removeDuressVault()
