262→
263→    private func setAsDuressVault() {
264→        guard let key = appState.currentVaultKey else { return }
265→        EmbraceManager.shared.addBreadcrumb(category: "settings.duressToggled")
266→        Task {
267→            do {
268→                try await DuressHandler.shared.setAsDuressVault(key: key.rawBytes)
269→            } catch {
270→                EmbraceManager.shared.captureError(error)
271→            }
272→        }
273→    }
274→
275→    private func removeDuressVault() {
276→        EmbraceManager.shared.addBreadcrumb(category: "settings.duressToggled")
277→        Task {
278→            await DuressHandler.shared.clearDuressVault()
279→        }
280→    }
281→
282→    private func deleteVault() {
283→        guard let key = appState.currentVaultKey else {
284→            dismiss()
285→            return
286→        }
287→
288→        // Delete all files and the vault index
289→        do {
290→            let index = try VaultStorage.shared.loadIndex(with: key)
291→            for file in index.files where !file.isDeleted {
292→                do {
293→                    try VaultStorage.shared.deleteFile(id: file.fileId, with: key)
294→                } catch {
295→                    vaultSettingsLogger.error("Failed to delete file \(file.fileId, privacy: .public): \(error.localizedDescription, privacy: .public)")
296→                    EmbraceManager.shared.captureError(error, context: ["action": "deleteFile", "fileId": file.fileId])
297→                }
298→            }
299→
300→            // Revoke any active CloudKit shares to invalidate all recipient copies
301→            if let shares = index.activeShares {
302→                for share in shares {
303→                    Task {
304→                        do {
305→                            // Revoke the share so recipients can no longer access it
306→                            try await CloudKitSharingManager.shared.revokeShare(shareVaultId: share.id)
307→                            vaultSettingsLogger.info("Revoked shared vault \(share.id, privacy: .public)")
308→                        } catch {
309→                            vaultSettingsLogger.error("Failed to revoke shared vault \(share.id, privacy: .public): \(error.localizedDescription, privacy: .public)")
310→                            EmbraceManager.shared.captureError(error, context: ["action": "revokeShare", "shareId": share.id])
311→                        }
312→                    }
313→                }
314→            }
315→
316→            // If this is a shared vault we joined, mark it as consumed
317→            if let isShared = index.isSharedVault, isShared, let vaultId = index.sharedVaultId {
318→                Task {
319→                    do {
320→                        try await CloudKitSharingManager.shared.markShareConsumed(shareVaultId: vaultId)
321→                        vaultSettingsLogger.info("Marked shared vault \(vaultId, privacy: .public) as consumed")
322→                    } catch {
323→                        vaultSettingsLogger.error("Failed to mark shared vault consumed \(vaultId, privacy: .public): \(error.localizedDescription, privacy: .public)")
324→                        EmbraceManager.shared.captureError(error, context: ["action": "markShareConsumed", "vaultId": vaultId])
325→                    }
326→                }
327→            }
328→
329→            try VaultStorage.shared.deleteVaultIndex(for: key)
330→        } catch {
331→            vaultSettingsLogger.error("Delete vault error: \(error.localizedDescription, privacy: .public)")
332→            EmbraceManager.shared.captureError(error, context: ["action": "deleteVault"])
333→        }
334→
335→        // Clean up recovery data and duress status
336→        Task {
337→            do {
338→                try await RecoveryPhraseManager.shared.deleteRecoveryData(for: key.rawBytes)
339→            } catch {
340→                vaultSettingsLogger.error("Failed to delete recovery data: \(error.localizedDescription, privacy: .public)")
341→                EmbraceManager.shared.captureError(error, context: ["action": "deleteRecoveryData"])
342→            }
343→            if await DuressHandler.shared.isDuressKey(key.rawBytes) {
344→                await DuressHandler.shared.clearDuressVault()
345→            }
346→        }
347→
348→        appState.lockVault()
349→        dismiss()
350→    }
351→    
352→    private func regenerateRecoveryPhrase() {
353→        guard let key = appState.currentVaultKey else { return }
354→        EmbraceManager.shared.addBreadcrumb(category: "settings.phraseRegenerated")
355→        Task {
356→            do {
357→                do {
358→                    _ = try await RecoveryPhraseManager.shared.regenerateRecoveryPhrase(for: key.rawBytes)
359→                } catch RecoveryError.vaultNotFound {
360→                    // No existing recovery data — create fresh entry
361→                    vaultSettingsLogger.info("No recovery data found, creating new recovery phrase")
362→                    let newPhrase = RecoveryPhraseGenerator.shared.generatePhrase()
363→                    try await RecoveryPhraseManager.shared.saveRecoveryPhrase(
364→                        phrase: newPhrase,
365→                        pattern: appState.currentPattern ?? [],
366→                        gridSize: 5,
367→                        patternKey: key.rawBytes
368→                    )
369→                }
370→                vaultSettingsLogger.debug("Recovery phrase regenerated")
371→                await MainActor.run {
372→                    showingRegeneratedPhrase = true
373→                }
374→            } catch {
375→                vaultSettingsLogger.error("Failed to regenerate phrase: \(error.localizedDescription, privacy: .public)")
376→                await MainActor.run {
377→                    regenerateErrorMessage = "Failed to regenerate recovery phrase. Please try again."
378→                }
379→            }
380→        }
381→    }
382→    
383→    private func loadVaultStatistics() {
384→        guard let key = appState.currentVaultKey else { return }
385→        
386→        Task {
387→            do {
388→                let result = try VaultStorage.shared.listFilesLightweight(with: key)
389→                let files = result.files
390→                let totalSize = files.reduce(0) { $0 + Int64($1.size) }
391→                
392→                // Check if this is the duress vault
393→                let ownerFingerprint = KeyDerivation.keyFingerprint(from: key.rawBytes)
394→                let duressInitiallyEnabled = await DuressHandler.shared.isDuressKey(key.rawBytes)
395→
396→                // Load sharing info
397→                let index = try VaultStorage.shared.loadIndex(with: key)
398→                let shared = index.isSharedVault ?? false
399→                let shareCount = index.activeShares?.count ?? 0
400→                let activeUploadJobs = await MainActor.run {
401→                    ShareUploadManager.shared.jobs(forOwnerFingerprint: ownerFingerprint)
402→                        .filter { $0.canTerminate }
403→                }
404→                let uploadCount = activeUploadJobs.count
405→                let sharingDisablesDuress = Self.shouldDisableDuressForSharing(
406→                    isSharedVault: shared,
407→                    activeShareCount: shareCount,
408→                    activeUploadCount: uploadCount
409→                )
410→                if duressInitiallyEnabled && sharingDisablesDuress {
411→                    await DuressHandler.shared.clearDuressVault()
412→                    vaultSettingsLogger.info("Cleared duress because this vault has active sharing")
413→                }
414→                let isDuress = duressInitiallyEnabled && !sharingDisablesDuress
415→
416→                await MainActor.run {
417→                    fileCount = files.count
418→                    storageUsed = totalSize
419→                    isDuressVault = isDuress
420→                    isSharedVault = shared
421→                    activeShareCount = shareCount
422→                    activeUploadCount = uploadCount
423→                }
424→            } catch {
425→                vaultSettingsLogger.error("Failed to load vault statistics: \(error.localizedDescription, privacy: .public)")
426→                await MainActor.run {
427→                    fileCount = 0
428→                    storageUsed = 0
429→                    activeUploadCount = 0
430→                }
431→            }
432→        }
433→    }
434→
435→    static func shouldDisableDuressForSharing(
436→        isSharedVault: Bool,
437→        activeShareCount: Int,
438→        activeUploadCount: Int
439→    ) -> Bool {
440→        isSharedVault || activeShareCount > 0 || activeUploadCount > 0
441→    }
442→    
443→    private static let byteCountFormatter: ByteCountFormatter = {
444→        let formatter = ByteCountFormatter()
445→        formatter.countStyle = .file
446→        formatter.allowedUnits = [.useBytes, .useKB, .useMB, .useGB]
447→        return formatter
448→    }()
449→
450→    private func formatBytes(_ bytes: Int64) -> String {
451→        Self.byteCountFormatter.string(fromByteCount: bytes)
452→    }
453→}
454→
