                .accessibilityIdentifier("settings_regen_phrase")

                Button("Set custom recovery phrase") {
                    showingCustomPhraseInput = true
                }
                .accessibilityIdentifier("settings_custom_phrase")
            }

            // Sharing
            Section {
                if isSharedVault {
