 3→struct PatternSetupCoordinator {
 4→    var deriveKey: ([Int], Int) async throws -> Data = { pattern, gridSize in
 5→        try await KeyDerivation.deriveKey(from: pattern, gridSize: gridSize)
 6→    }
 7→    var vaultExists: (Data) -> Bool = { key in
 8→        VaultStorage.shared.vaultHasFiles(for: VaultKey(key))
 9→    }
10→    var saveIndex: (VaultStorage.VaultIndex, Data) throws -> Void = { index, key in
11→        try VaultStorage.shared.saveIndex(index, with: VaultKey(key))
12→    }
13→    var saveRecoveryPhrase: (String, [Int], Int, Data) async throws -> Void = { phrase, pattern, gridSize, key in
14→        try await RecoveryPhraseManager.shared.saveRecoveryPhrase(
15→            phrase: phrase, pattern: pattern, gridSize: gridSize, patternKey: key
16→        )
17→    }
18→
19→    enum SetupResult {
20→        case success(key: Data)
21→        case duplicatePattern
22→        case error(String)
23→    }
24→
25→    /// Save a new pattern: derive key, check uniqueness, create vault index, save recovery phrase.
26→    func savePattern(_ pattern: [Int], gridSize: Int, phrase: String) async -> SetupResult {
27→        do {
28→            let key = try await deriveKey(pattern, gridSize)
29→
30→            if vaultExists(key) {
31→                return .duplicatePattern
32→            }
33→
34→            // Create empty vault index. loadIndex auto-creates a proper v3 index with master key when none exists.
35→            let emptyIndex = try VaultStorage.shared.loadIndex(with: VaultKey(key))
36→            try saveIndex(emptyIndex, key)
37→
38→            try await saveRecoveryPhrase(phrase, pattern, gridSize, key)
39→
40→            return .success(key: key)
41→        } catch {
42→            return .error(error.localizedDescription)
43→        }
44→    }
45→
46→    /// Save a custom recovery phrase for an already-created vault.
47→    func saveCustomPhrase(_ phrase: String, pattern: [Int], gridSize: Int, key: Data) async -> SetupResult {
48→        do {
49→            try await saveRecoveryPhrase(phrase, pattern, gridSize, key)
50→            return .success(key: key)
51→        } catch {
52→            return .error(error.localizedDescription)
53→        }
54→    }
55→}
