297→                .disabled(useCustomPhrase && !(customPhraseValidation?.isAcceptable ?? false))
298→                .accessibilityIdentifier("recovery_saved")
299→                .alert("Are you sure?", isPresented: $showSaveConfirmation) {
300→                    Button("Cancel", role: .cancel) { /* No-op */ }
301→                    Button("Yes, I've saved it") {
302→                        if useCustomPhrase {
303→                            if let validation = customPhraseValidation, validation.isAcceptable {
304→                                saveCustomRecoveryPhrase()
305→                            }
306→                        } else {
307→                            onComplete()
308→                        }
309→                    }
310→                } message: {
311→                    Text("This recovery phrase will NEVER be shown again. Make sure you've written it down and stored it safely.")
312→                }
