317→    // MARK: - Actions
318→
319→    private func handlePatternComplete(_ pattern: [Int]) {
320→        patternSetupLogger.debug("Pattern completed in \(String(describing: step), privacy: .public) step, count: \(pattern.count)")
321→        
322→        switch step {
323→        case .create:
324→            let result = PatternValidator.shared.validate(pattern, gridSize: patternState.gridSize)
325→            validationResult = result
326→
327→            patternSetupLogger.debug("Validation: isValid=\(result.isValid), errors=\(result.errors.count), warnings=\(result.warnings.count)")
328→
329→            if result.isValid {
330→                UINotificationFeedbackGenerator().notificationOccurred(.success)
331→                errorMessage = nil
332→                firstPattern = pattern
333→                step = .confirm
334→                patternState.reset()
335→
336→                patternSetupLogger.debug("Pattern valid, moving to confirm step")
337→            } else {
338→                patternSetupLogger.debug("Pattern invalid, resetting")
339→                patternState.reset()
340→            }
341→
342→        case .confirm:
343→            patternSetupLogger.debug("Confirming pattern, match=\(pattern == firstPattern)")
344→            
345→            if pattern == firstPattern {
346→                // Patterns match - save and continue
347→                UINotificationFeedbackGenerator().notificationOccurred(.success)
348→                errorMessage = nil
349→                patternSetupLogger.debug("Patterns match, saving")
350→                // Generate the phrase now, before saving
351→                if !useCustomPhrase {
352→                    generatedPhrase = RecoveryPhraseGenerator.shared.generatePhrase()
353→                }
354→                savePattern(pattern)
355→            } else {
356→                // Patterns don't match - show error and reset
357→                UINotificationFeedbackGenerator().notificationOccurred(.error)
358→                patternSetupLogger.debug("Patterns don't match, resetting")
359→                errorMessage = "Patterns don't match. Try again."
360→                patternState.reset()
361→                Task {
362→                    try? await Task.sleep(nanoseconds: 2_500_000_000)
363→                    await MainActor.run { errorMessage = nil }
364→                }
365→            }
366→
367→        default:
368→            break
369→        }
370→    }
371→
372→    private func savePattern(_ pattern: [Int]) {
373→        patternSetupLogger.debug("Saving pattern, gridSize=\(patternState.gridSize)")
374→        isSaving = true
375→
376→        Task {
377→            let phrase = useCustomPhrase ? customPhrase.trimmingCharacters(in: .whitespacesAndNewlines) : generatedPhrase
378→            let result = await coordinator.savePattern(pattern, gridSize: patternState.gridSize, phrase: phrase)
379→
380→            await MainActor.run {
381→                switch result {
382→                case .success(let key):
383→                    patternSetupLogger.debug("Pattern saved successfully")
384→                    EmbraceManager.shared.addBreadcrumb(category: "onboarding.complete", data: ["gridSize": patternState.gridSize])
385→
386→                    isSaving = false
387→                    appState.currentVaultKey = VaultKey(key)
388→                    let letters = GridLetterManager.shared.vaultName(for: pattern)
389→                    appState.updateVaultName(letters.isEmpty ? "Vault" : "Vault \(letters)")
390→                    step = .recovery
391→
392→                case .duplicatePattern:
393→                    patternSetupLogger.info("Vault already exists for this pattern")
394→                    isSaving = false
395→                    step = .create
396→                    patternState.reset()
397→                    firstPattern = []
398→                    validationResult = PatternValidationResult(
399→                        isValid: false,
400→                        errors: [.custom("This pattern is already used by another vault. Please choose a different pattern.")],
401→                        warnings: [],
402→                        metrics: PatternSerializer.PatternMetrics(
403→                            nodeCount: pattern.count,
404→                            directionChanges: 0,
405→                            startsAtCorner: (Set(pattern).count != 0),
406→                            endsAtCorner: false,
407→                            crossesCenter: false,
408→                            touchesAllQuadrants: false
409→                        )
410→                    )
411→
412→                case .error(let message):
413→                    patternSetupLogger.error("Error saving pattern: \(message, privacy: .public)")
414→                    isSaving = false
415→                    errorMessage = "Failed to save pattern. Please try again."
416→                    step = .create
417→                    patternState.reset()
418→                    firstPattern = []
419→                }
420→            }
421→        }
422→    }
423→    
424→    private func saveCustomRecoveryPhrase() {
425→        guard let key = appState.currentVaultKey else { return }
426→        isSaving = true
427→
428→        Task {
429→            let phrase = customPhrase.trimmingCharacters(in: .whitespacesAndNewlines)
430→            let result = await coordinator.saveCustomPhrase(phrase, pattern: firstPattern, gridSize: patternState.gridSize, key: key.rawBytes)
431→
432→            await MainActor.run {
433→                isSaving = false
434→                switch result {
435→                case .success:
436→                    onComplete()
437→                case .duplicatePattern:
438→                    break // Not possible for custom phrase path
439→                case .error(let message):
440→                    patternSetupLogger.error("Failed to save custom phrase: \(message, privacy: .public)")
441→                    errorMessage = "Failed to save recovery phrase. Please try again."
442→                }
443→            }
444→        }
445→    }
446→
447→    private func validateCustomPhrase(_ phrase: String) {
448→        guard !phrase.isEmpty else {
449→            customPhraseValidation = nil
450→            return
451→        }
452→        customPhraseValidation = RecoveryPhraseGenerator.shared.validatePhrase(phrase)
453→    }
454→
455→    private func dismissKeyboard() {
456→        UIApplication.shared.sendAction(
457→            #selector(UIResponder.resignFirstResponder),
458→            to: nil,
459→            from: nil,
460→            for: nil
461→        )
462→    }
463→}
