  3→enum OnboardingStep: Int, CaseIterable {
  4→    case welcome
  5→    case permissions
  6→    case analytics
  7→    case paywall
  8→    case thankYou
  9→    case rating
 10→
 11→    var progressFraction: CGFloat {
 12→        CGFloat(rawValue + 1) / CGFloat(Self.allCases.count)
 13→    }
 14→
 15→    func next() -> OnboardingStep? {
 16→        OnboardingStep(rawValue: rawValue + 1)
 17→    }
 18→
 19→    func previous() -> OnboardingStep? {
 20→        OnboardingStep(rawValue: rawValue - 1)
 21→    }
 22→}
 23→
 24→struct OnboardingView: View {
 25→    /// When set, the view is shown in replay mode: pattern setup is skipped and
 26→    /// tapping "Continue" on the final step calls this closure instead.
 27→    var onReplayDismiss: (() -> Void)? = nil
 28→
 29→    @Environment(AppState.self) private var appState
 30→    @Environment(\.accessibilityReduceMotion) private var reduceMotion
 31→    @State private var currentStep: OnboardingStep = .welcome
 32→    @State private var showPatternSetup = false
 33→
 34→    private var isReplay: Bool { onReplayDismiss != nil }
 35→
 36→    var body: some View {
 37→        if showPatternSetup {
 38→            PatternSetupView(onComplete: { completeOnboarding() })
 39→        } else {
 40→            VStack(spacing: 0) {
 41→                // Progress bar + back arrow (+ close button in replay mode)
 42→                HStack(spacing: 8) {
 43→                    Button {
 44→                        if let prev = currentStep.previous() {
 45→                            withAnimation(animation) { currentStep = prev }
 46→                        }
 47→                    } label: {
 48→                        Image(systemName: "chevron.left")
 49→                            .font(.body.weight(.semibold))
 50→                            .foregroundStyle(.vaultText)
 51→                    }
 52→                    .opacity(currentStep != .welcome ? 1 : 0)
 53→                    .disabled(currentStep == .welcome)
 54→                    .accessibilityIdentifier("onboarding_back")
 55→
 56→                    Capsule()
 57→                        .fill(Color.vaultSecondaryText.opacity(0.2))
 58→                        .frame(height: 4)
 59→                        .overlay(alignment: .leading) {
 60→                            Capsule()
 61→                                .fill(Color.accentColor)
 62→                                .frame(
 63→                                    width: nil,
 64→                                    height: 4
 65→                                )
 66→                                .frame(
 67→                                    maxWidth: .infinity,
 68→                                    alignment: .leading
 69→                                )
 70→                                .scaleEffect(x: currentStep.progressFraction, y: 1, anchor: .leading)
 71→                        }
 72→
 73→                    if currentStep == .paywall {
 74→                        Button("Skip") { advance() }
 75→                            .font(.subheadline.weight(.medium))
 76→                            .foregroundStyle(.vaultSecondaryText)
 77→                            .accessibilityIdentifier("paywall_skip")
 78→                    } else if isReplay {
 79→                        Button {
 80→                            onReplayDismiss?()
 81→                        } label: {
 82→                            Image(systemName: "xmark")
 83→                                .font(.body.weight(.semibold))
 84→                                .foregroundStyle(.vaultSecondaryText)
 85→                        }
 86→                        .accessibilityLabel("Close")
 87→                        .accessibilityIdentifier("onboarding_replay_close")
 88→                    }
 89→                }
 90→                .padding(.horizontal, 20)
 91→                .padding(.top, 12)
 92→                .padding(.bottom, 8)
 93→
 94→                // Step content
 95→                switch currentStep {
 96→                case .welcome:
 97→                    WelcomeView(onContinue: { advance() })
 98→                case .permissions:
 99→                    PermissionsView(onContinue: { advance() })
100→                case .analytics:
101→                    AnalyticsConsentView(onContinue: { advance() })
102→                case .paywall:
103→                    PaywallStepView(onContinue: { advance() })
104→                case .thankYou:
105→                    ThankYouView(onContinue: { advance() })
106→                case .rating:
107→                    RatingView(onContinue: {
108→                        if let dismiss = onReplayDismiss {
109→                            dismiss()
110→                        } else {
111→                            withAnimation(animation) { showPatternSetup = true }
112→                        }
113→                    })
114→                }
115→            }
116→            .background(Color.vaultBackground.ignoresSafeArea())
117→        }
118→    }
119→
120→    private var animation: Animation? {
121→        reduceMotion ? nil : .easeInOut
122→    }
123→
124→    private func advance() {
125→        if let next = currentStep.next() {
126→            withAnimation(animation) { currentStep = next }
127→        }
128→    }
129→
130→    private func completeOnboarding() {
131→        Task {
132→            await appState.completeOnboarding()
133→        }
134→    }
135→}
