import SwiftUI
import RevenueCatUI

struct AnalyticsConsentView: View {
    let onContinue: () -> Void

    @Environment(SubscriptionManager.self) private var subscriptionManager
    @State private var consentChosen = false

    var body: some View {
        if consentChosen {
            // Paywall phase — skip if RevenueCat not configured (no production key)
            if subscriptionManager.isConfigured {
                VStack(spacing: 0) {
                    PaywallView()
                        .onPurchaseCompleted { info in
                            subscriptionManager.updateFromCustomerInfo(info)
                            onContinue()
                        }
                        .onRestoreCompleted { info in
                            subscriptionManager.updateFromCustomerInfo(info)
                            onContinue()
                        }

                    Button(action: onContinue) {
                        Text("Skip")
                            .font(.subheadline)
                            .foregroundStyle(.vaultSecondaryText)
                    }
                    .accessibilityIdentifier("paywall_skip")
                    .padding(.bottom, 24)
                }
            } else {
                Color.clear.onAppear { onContinue() }
            }
        } else {
            // Consent phase
            VStack(spacing: 32) {
                Spacer()

