struct AnalyticsConsentView: View {
    let onContinue: () -> Void

    @Environment(SubscriptionManager.self) private var subscriptionManager
    @State private var consentChosen = false

    var body: some View {
        if consentChosen {
            // Paywall phase — skip if RevenueCat not configured (no production key)
            if subscriptionManager.isConfigured {
                VStack(spacing: 0) {
                    PaywallView()
                        .onPurchaseCompleted { info in
                            subscriptionManager.updateFromCustomerInfo(info)
                            onContinue()
                        }
                        .onRestoreCompleted { info in
                            subscriptionManager.updateFromCustomerInfo(info)
                            onContinue()
                        }

                    Button(action: onContinue) {
                        Text("Skip")
                            .font(.subheadline)
                            .foregroundStyle(.vaultSecondaryText)
                    }
                    .accessibilityIdentifier("paywall_skip")
                    .padding(.bottom, 24)
                }
            } else {
                Color.clear.onAppear { onContinue() }
            }
        } else {
            // Consent phase
            VStack(spacing: 32) {
                Spacer()

                Image(systemName: "chart.bar.xaxis.ascending")
                    .font(.system(size: 56))
                    .foregroundStyle(.tint)

                Text("Help Improve Vaultaire")
                    .font(.title)
                    .fontWeight(.bold)

                VStack(spacing: 12) {
                    Text("Share anonymous crash reports and usage statistics to help us make Vaultaire better.")
                        .font(.body)
                        .multilineTextAlignment(.center)

                    Text("No personal data is collected. You can change this anytime in Settings.")
                        .font(.subheadline)
                        .foregroundStyle(.vaultSecondaryText)
                        .multilineTextAlignment(.center)
                }
                .padding(.horizontal, 24)

                Spacer()

                VStack(spacing: 12) {
                    Button(action: {
                        AnalyticsManager.shared.setEnabled(true)
                        consentChosen = true
                    }) {
                        Text("Enable Analytics")
                            .font(.headline)
                            .frame(maxWidth: .infinity)
                            .padding()
                    }
                    .vaultProminentButtonStyle()
                    .accessibilityIdentifier("analytics_enable")

                    Button(action: {
                        consentChosen = true
                    }) {
                        Text("No Thanks")
                            .font(.headline)
                            .frame(maxWidth: .infinity)
                            .padding()
                    }
                    .vaultSecondaryButtonStyle()
                    .accessibilityIdentifier("analytics_decline")
                }
                .padding(.horizontal, 40)
                .padding(.bottom, 40)
            }
        }
    }
}

