import SwiftUI
import RevenueCatUI

struct OnboardingView: View {
    @Environment(AppState.self) private var appState
    @Environment(\.accessibilityReduceMotion) private var reduceMotion
    @State private var currentStep = 0

    var body: some View {
        VStack {
            switch currentStep {
            case 0:
                WelcomeView(
                    onContinue: { currentStep = 1 }
                )
            case 1:
                PatternSetupView(onComplete: { currentStep = 2 })
            case 2:
                PermissionsView(
                    onContinue: { currentStep = 3 }
                )
            case 3:
                AnalyticsConsentView(
                    onContinue: { completeOnboarding() }
                )
            default:
                EmptyView()
            }
        }
        .animation(reduceMotion ? nil : .easeInOut, value: currentStep)
    }

    private func completeOnboarding() {
        appState.completeOnboarding()
    }
}

struct FeatureRow: View {
    let icon: String
    let title: String
    let description: String

    @Environment(\.dynamicTypeSize) private var dynamicTypeSize

    var body: some View {
        let useVertical = dynamicTypeSize.isAccessibilitySize

        Group {
            if useVertical {
                VStack(alignment: .leading, spacing: 8) {
                    featureIcon
                    featureText
                }
            } else {
                HStack(alignment: .top, spacing: 16) {
                    featureIcon
                    featureText
                }
            }
        }
        .accessibilityElement(children: .combine)
    }

    private var featureIcon: some View {
        Image(systemName: icon)
            .font(.title2)
            .foregroundStyle(.tint)
            .frame(width: 32)
            .accessibilityHidden(true)
    }

    private var featureText: some View {
        VStack(alignment: .leading, spacing: 4) {
            Text(title)
                .font(.headline)

            Text(description)
                .font(.subheadline)
                .foregroundStyle(.vaultSecondaryText)
        }
    }
}

#Preview {
    OnboardingView()
        .environment(AppState())
        .environment(SubscriptionManager.shared)
}

import SwiftUI
import CloudKit
import RevenueCatUI
import os.log

// MARK: - App Settings Destination

enum AppSettingsDestination: Hashable {
    case duressPattern
    case iCloudBackup
    case restoreBackup
}

private let settingsLogger = Logger(subsystem: "app.vaultaire.ios", category: "Settings")

// MARK: - App Settings View

struct AppSettingsView: View {
    @Environment(\.dismiss) private var dismiss
    @Environment(AppState.self) private var appState
    @Environment(SubscriptionManager.self) private var subscriptionManager

    @AppStorage("showPatternFeedback") private var showFeedback = true
    @AppStorage("analyticsEnabled") private var analyticsEnabled = false

    @State private var showingNuclearConfirmation = false
    @State private var showingPaywall = false
    @State private var showingCustomerCenter = false
    @State private var isRestoringPurchases = false
    @State private var showingClearStagedConfirmation = false
    @State private var pendingStagedSize: Int64 = 0

    #if DEBUG
    @State private var showingDebugResetConfirmation = false
    #endif

    var body: some View {
        List {
            // Premium
            if subscriptionManager.isPremium {
                Section("Premium") {
                    HStack {
                        Image(systemName: "crown.fill")
                            .foregroundStyle(.yellow)
                        Text("Vaultaire Pro")
                        Spacer()
                        Text("Active")
                            .font(.caption)
                            .foregroundStyle(.green)
                    }

                    Button("Manage Subscription") {
                        showingCustomerCenter = true
                    }
                }
            } else {
                Section {
                    VStack(alignment: .leading, spacing: 12) {
                        HStack {
                            Image(systemName: "crown.fill")
                                .font(.title2)
                                .foregroundStyle(.yellow)
                            Text("Unlock Vaultaire Pro")
                                .font(.headline)
                        }

                        VStack(alignment: .leading, spacing: 6) {
