        defer { if wasOptimized { try? FileManager.default.removeItem(at: outputURL) } }

        XCTAssertFalse(wasOptimized, "HEVC ≤1080p must skip transcode even at high bitrate")
        XCTAssertEqual(outputURL, videoURL)
    }

