  3→// MARK: - Vault Color Palette
  4→//
  5→// Design tokens (light mode):
  6→//   Background:       #d1d1e9  (lavender)
  7→//   Surface / Card:   #fffffe  (near-white)
  8→//   Text / Headline:  #2b2c34  (dark charcoal)
  9→//   Accent / Link:    #6246ea  (indigo-purple)  — set via AccentColor asset
 10→//   Highlight:        #e45858  (coral red)
 11→//   Button text:      #fffffe  (white on accent)
 12→//
 13→// Colors are defined as asset catalog color sets and auto-generated
 14→// by Xcode as Color.vaultBackground, .vaultSurface, .vaultText,
 15→// .vaultSecondaryText, .vaultHighlight.
 16→
 17→// MARK: - Convenience View Modifiers
 18→
 19→extension View {
 20→    /// Applies the vault background color to the view.
 21→    func vaultBackgroundStyle() -> some View {
 22→        self.background(Color.vaultBackground.ignoresSafeArea())
 23→    }
 24→
 25→    /// Glass card background on iOS 26+, solid surface fallback on earlier.
 26→    func vaultGlassBackground(cornerRadius: CGFloat = 12) -> some View {
 27→        Group {
 28→            if #available(iOS 26, *) {
 29→                self.glassEffect(.regular, in: .rect(cornerRadius: cornerRadius))
 30→            } else {
 31→                self.background(Color.vaultSurface)
 32→                    .clipShape(RoundedRectangle(cornerRadius: cornerRadius))
 33→            }
 34→        }
 35→    }
 36→
 37→    /// Glass card with tint on iOS 26+, tinted background fallback.
 38→    func vaultGlassTintedBackground(tint: Color, cornerRadius: CGFloat = 12) -> some View {
 39→        Group {
 40→            if #available(iOS 26, *) {
 41→                self.glassEffect(.regular.tint(tint), in: .rect(cornerRadius: cornerRadius))
 42→            } else {
 43→                self.background(tint.opacity(0.1))
 44→                    .clipShape(RoundedRectangle(cornerRadius: cornerRadius))
 45→            }
 46→        }
 47→    }
 48→
 49→    /// Primary CTA: glassProminent on iOS 26+, borderedProminent fallback.
 50→    func vaultProminentButtonStyle() -> some View {
 51→        Group {
 52→            if #available(iOS 26, *) {
 53→                self.buttonStyle(.glassProminent)
 54→            } else {
 55→                self.buttonStyle(.borderedProminent)
 56→            }
 57→        }
 58→    }
 59→
 60→    /// Secondary button: glass on iOS 26+, bordered fallback.
 61→    func vaultSecondaryButtonStyle() -> some View {
 62→        Group {
 63→            if #available(iOS 26, *) {
 64→                self.buttonStyle(.glass)
 65→            } else {
 66→                self.buttonStyle(.bordered)
 67→            }
 68→        }
 69→    }
 70→
 71→    /// Floating bar material: removes thinMaterial on iOS 26+ (glass buttons provide their own chrome).
 72→    func vaultBarMaterial() -> some View {
 73→        Group {
 74→            if #available(iOS 26, *) {
 75→                self
 76→            } else {
 77→                self.background(.thinMaterial)
 78→            }
 79→        }
 80→    }
 81→
 82→    /// Full-width banner: glass on iOS 26+, ultraThinMaterial fallback.
 83→    func vaultBannerBackground() -> some View {
 84→        Group {
 85→            if #available(iOS 26, *) {
 86→                self.glassEffect(.regular, in: .rect(cornerRadius: 0))
 87→            } else {
 88→                self.background(.ultraThinMaterial)
 89→            }
 90→        }
 91→    }
 92→
 93→    /// Glass orb for icon circles on iOS 26+, no-op fallback (existing fill remains).
 94→    func vaultGlassOrb() -> some View {
 95→        Group {
 96→            if #available(iOS 26, *) {
 97→                self.glassEffect(.regular.tint(Color.accentColor), in: .circle)
 98→            } else {
 99→                self
100→            }
101→        }
102→    }
103→
104→    /// Pattern grid background: glass on iOS 26+, translucent surface fallback.
105→    func vaultPatternGridBackground() -> some View {
106→        Group {
107→            if #available(iOS 26, *) {
108→                self.glassEffect(.regular, in: .rect(cornerRadius: 16))
109→            } else {
110→                self.background(
111→                    RoundedRectangle(cornerRadius: 16)
112→                        .fill(Color.vaultSurface.opacity(0.3))
113→                )
114→            }
115→        }
116→    }
117→}
