   214// MARK: - Quick Look Preview
   215
   216struct QuickLookPreview: UIViewControllerRepresentable {
   217    let url: URL
   218
   219    func makeUIViewController(context: Context) -> QLPreviewController {
   220        let controller = QLPreviewController()
   221        controller.dataSource = context.coordinator
   222        // Hide the default nav bar  our custom header handles Done/Actions
   223        controller.navigationItem.leftBarButtonItems = []
   224        controller.navigationItem.rightBarButtonItems = []
   225        return controller
   226    }
   227
   228    func updateUIViewController(_ _: QLPreviewController, context _: Context) { /* No update needed */ }
   229
   230    func makeCoordinator() -> Coordinator {
   231        Coordinator(url: url)
   232    }
   233
   234    class Coordinator: NSObject, QLPreviewControllerDataSource {
   235        let url: URL
   236
   237        init(url: URL) {
   238            self.url = url
   239        }
   240
   241        func numberOfPreviewItems(in _: QLPreviewController) -> Int { 1 }
   242
   243        func previewController(_ _: QLPreviewController, previewItemAt _: Int) -> QLPreviewItem {
   244            url as QLPreviewItem
   245        }
   246    }
   247}
