349→        case .verifyCurrent:
350→            verifyCurrentPattern(pattern)
351→
352→        case .createNew:
353→            validateNewPattern(pattern)
354→
355→        case .confirmNew:
356→            confirmNewPattern(pattern)
357→
358→        case .complete:
359→            break
360→        }
361→    }
