363→    private func verifyCurrentPattern(_ pattern: [Int]) {
364→        guard let currentKey = appState.currentVaultKey else {
365→            flow.showError("No vault key available")
366→            patternState.reset()
367→            return
368→        }
369→
370→        // Validate pattern structure first
371→        let validation = PatternValidator.shared.validate(pattern, gridSize: patternState.gridSize)
372→        if !validation.isValid {
373→            flow.showError(validation.errors.first?.message ?? "Invalid pattern")
374→            patternState.reset()
375→            return
376→        }
377→
378→        guard flow.beginProcessingIfIdle() else { return }
379→
380→        Task {
381→            do {
382→                // Derive key from entered pattern and check if it matches current key
383→                let enteredKey = try await KeyDerivation.deriveKey(from: pattern, gridSize: patternState.gridSize)
384→
385→                await MainActor.run {
386→                    if enteredKey == currentKey.rawBytes {
387→                        // Pattern verified - move to next step
388→                        changePatternLogger.debug("Current pattern verified")
389→                        flow.transitionToCreate(currentPattern: pattern)
390→                        patternState.reset()
391→                    } else {
392→                        // Pattern doesn't match
393→                        changePatternLogger.debug("Current pattern incorrect")
394→                        flow.showError("Incorrect pattern. Please try again.")
395→                        patternState.reset()
396→                    }
397→                    flow.endProcessing()
398→                }
399→            } catch {
400→                await MainActor.run {
401→                    flow.showError("Error verifying pattern: \(error.localizedDescription)")
402→                    patternState.reset()
403→                    flow.endProcessing()
404→                }
405→            }
406→        }
407→    }
408→
409→    private func validateNewPattern(_ pattern: [Int]) {
410→        guard flow.beginProcessingIfIdle() else { return }
411→
412→        Task {
413→            // First, validate the pattern structure
414→            let result = PatternValidator.shared.validate(pattern, gridSize: patternState.gridSize)
415→
416→            if result.isValid {
417→                // Pattern valid — don't show feedback yet (avoids brief flash before transition)
418→                do {
419→                    let newKey = try await KeyDerivation.deriveKey(from: pattern, gridSize: patternState.gridSize)
420→                    let hasFiles = VaultStorage.shared.vaultHasFiles(for: VaultKey(newKey))
421→
422→                    await MainActor.run {
423→                        if hasFiles {
424→                            changePatternLogger.info("Pattern already used by a vault with files")
425→                            flow.showError("This pattern is already used by another vault. Please choose a different pattern.")
426→                            patternState.reset()
427→                        } else {
428→                            changePatternLogger.debug("New pattern valid and unique")
429→                            flow.transitionToConfirm(newPattern: pattern)
430→                            patternState.reset()
431→                        }
432→                        flow.endProcessing()
433→                    }
434→                } catch {
435→                    await MainActor.run {
436→                        flow.showError("Error checking pattern: \(error.localizedDescription)")
437→                        patternState.reset()
438→                        flow.endProcessing()
439→                    }
440→                }
441→            } else {
442→                changePatternLogger.debug("New pattern invalid")
443→                await MainActor.run {
444→                    flow.showValidation(result)
445→                    patternState.reset()
446→                    flow.endProcessing()
447→                }
448→            }
449→        }
450→    }
451→
452→    private func confirmNewPattern(_ pattern: [Int]) {
453→        let validation = PatternValidator.shared.validate(pattern, gridSize: patternState.gridSize)
454→
455→        guard validation.isValid else {
456→            flow.showError("Invalid pattern. Please try again.")
457→            patternState.reset()
458→            return
459→        }
460→
461→        // Check if pattern matches the new pattern
462→        if pattern != flow.newPattern {
463→            flow.showError("Patterns don't match. Try again.")
464→            patternState.reset()
465→
466→            // Auto-clear error after 2.5s
467→            Task {
468→                try? await Task.sleep(for: .seconds(2.5))
469→                await MainActor.run {
470→                    if flow.errorMessage == "Patterns don't match. Try again." {
471→                        flow.clearFeedback()
472→                    }
473→                }
474→            }
475→            return
476→        }
477→
478→        // Patterns match — show confirmation
479→        showSaveConfirmation = true
480→    }
481→
482→    private func completePatternChange() {
483→        guard let currentKey = appState.currentVaultKey,
484→              !flow.newPattern.isEmpty else {
485→            return
486→        }
487→
488→        guard flow.beginProcessingIfIdle() else { return }
489→
490→        Task {
491→            do {
492→                // Derive new key from new pattern
493→                let newKey = try await KeyDerivation.deriveKey(from: flow.newPattern, gridSize: patternState.gridSize)
494→
495→                // Change the vault key
496→                try VaultStorage.shared.changeVaultKey(from: currentKey, to: VaultKey(newKey))
497→
498→                // Update app state with new key and pattern
499→                await MainActor.run {
500→                    appState.currentVaultKey = VaultKey(newKey)
501→                    appState.currentPattern = flow.newPattern
502→                }
503→
504→                // Regenerate recovery phrase
505→                let newPhrase = RecoveryPhraseGenerator.shared.generatePhrase()
506→                try await RecoveryPhraseManager.shared.saveRecoveryPhrase(
507→                    phrase: newPhrase,
508→                    pattern: flow.newPattern,
509→                    gridSize: 5,
510→                    patternKey: newKey
511→                )
512→
513→                await MainActor.run {
514→                    flow.complete(with: newPhrase)
515→                }
516→
517→                // Force iCloud backup with new key so backup matches new pattern
518→                if UserDefaults.standard.bool(forKey: "iCloudBackupEnabled") {
519→                    UserDefaults.standard.set(0, forKey: "lastBackupTimestamp")
520→                    await MainActor.run {
521→                        iCloudBackupManager.shared.performBackupIfNeeded(with: newKey)
522→                    }
523→                    changePatternLogger.info("Triggered iCloud backup after pattern change")
524→                }
525→
526→                changePatternLogger.info("Pattern changed successfully")
527→
528→            } catch {
529→                changePatternLogger.error("Failed to change pattern: \(error.localizedDescription, privacy: .public)")
530→                await MainActor.run {
531→                    flow.showError("Failed to change pattern: \(error.localizedDescription)")
532→                    flow.endProcessing()
533→                }
534→            }
535→        }
536→    }
537→}
