  1→import SwiftUI
  2→import os.log
  3→
  4→private let changePatternLogger = Logger(subsystem: "app.vaultaire.ios", category: "ChangePattern")
  5→
  6→enum ChangePatternStep: Equatable {
  7→    case verifyCurrent
  8→    case createNew
  9→    case confirmNew
 10→    case complete
 11→}
 12→
 13→struct ChangePatternFlowState {
 14→    var step: ChangePatternStep = .verifyCurrent
 15→    var currentPattern: [Int] = []
 16→    var newPattern: [Int] = []
 17→    var validationResult: PatternValidationResult?
 18→    var errorMessage: String?
 19→    var isProcessing = false
 20→    var newRecoveryPhrase = ""
 21→
 22→    mutating func clearFeedback() {
 23→        validationResult = nil
 24→        errorMessage = nil
 25→    }
 26→
 27→    mutating func showValidation(_ result: PatternValidationResult) {
 28→        validationResult = result
 29→        errorMessage = nil
 30→    }
 31→
 32→    mutating func showError(_ message: String) {
 33→        errorMessage = message
 34→        validationResult = nil
 35→    }
 36→
 37→    mutating func beginProcessingIfIdle() -> Bool {
 38→        guard !isProcessing else { return false }
 39→        isProcessing = true
 40→        return true
 41→    }
 42→
 43→    mutating func endProcessing() {
 44→        isProcessing = false
 45→    }
 46→
 47→    mutating func resetForStartOver() {
 48→        step = .verifyCurrent
 49→        currentPattern = []
 50→        newPattern = []
 51→        clearFeedback()
 52→        isProcessing = false
 53→    }
 54→
 55→    mutating func skipVerification() {
 56→        step = .createNew
 57→        currentPattern = []
 58→        newPattern = []
 59→        clearFeedback()
 60→        isProcessing = false
 61→    }
 62→
 63→    mutating func skipVerifyForTesting(pattern: [Int]) {
 64→        step = .createNew
 65→        currentPattern = pattern
 66→        newPattern = []
 67→        clearFeedback()
 68→        isProcessing = false
 69→    }
 70→
 71→    mutating func transitionToCreate(currentPattern pattern: [Int]) {
 72→        currentPattern = pattern
 73→        step = .createNew
 74→        clearFeedback()
 75→        isProcessing = false
 76→    }
 77→
 78→    mutating func transitionToConfirm(newPattern pattern: [Int]) {
 79→        newPattern = pattern
 80→        step = .confirmNew
 81→        clearFeedback()
 82→        isProcessing = false
 83→    }
 84→
 85→    mutating func complete(with recoveryPhrase: String) {
 86→        step = .complete
 87→        newRecoveryPhrase = recoveryPhrase
 88→        clearFeedback()
 89→        isProcessing = false
 90→    }
 91→}
 92→
 93→struct ChangePatternView: View {
 94→    @Environment(\.dismiss) private var dismiss
 95→    @Environment(AppState.self) private var appState
 96→    
 97→    @State private var patternState = PatternState()
 98→    @State private var flow = ChangePatternFlowState()
 99→    @State private var showSaveConfirmation = false
100→
101→    var body: some View {
102→        NavigationStack {
103→            VStack(spacing: 24) {
104→                if step != .complete {
105→                    // Progress indicator
106→                    HStack(spacing: 8) {
107→                        ForEach(0..<totalSteps, id: \.self) { index in
108→                            Capsule()
109→                                .fill(stepIndex >= index ? Color.accentColor : Color.vaultSecondaryText.opacity(0.3))
110→                                .frame(width: 40, height: 4)
111→                        }
112→                    }
113→                    .padding(.top)
114→                    .accessibilityElement(children: .ignore)
115→                    .accessibilityLabel("Step \(stepIndex + 1) of \(totalSteps)")
116→                }
117→
118→                if isMaestroHookEnabled {
119→                    maestroChangePatternTestHooks
120→                }
121→
122→                if skipVerification && step != .complete {
123→                    HStack(spacing: 8) {
124→                        Image(systemName: "info.circle.fill")
125→                            .foregroundStyle(.blue)
126→                        Text("You unlocked with your recovery phrase, so pattern verification is not required.")
127→                            .font(.caption)
128→                            .foregroundStyle(.vaultSecondaryText)
129→                    }
130→                    .padding(12)
131→                    .frame(maxWidth: .infinity, alignment: .leading)
132→                    .vaultGlassBackground(cornerRadius: 10)
133→                    .padding(.horizontal, 4)
134→                    .accessibilityIdentifier("change_pattern_skip_info_banner")
135→                }
136→
137→                if step != .complete {
138→                    // Title and subtitle — fixed height prevents grid from shifting between steps
139→                    VStack(spacing: 8) {
140→                        Text(stepTitle)
141→                            .font(.title2)
142→                            .fontWeight(.bold)
143→
144→                        Text(stepSubtitle)
145→                            .font(.subheadline)
146→                            .foregroundStyle(.vaultSecondaryText)
147→                            .multilineTextAlignment(.center)
148→                            .fixedSize(horizontal: false, vertical: true)
149→                            .frame(minHeight: 44, alignment: .top)
150→                    }
151→                    .padding(.horizontal)
152→                }
153→
154→                // Content based on step
155→                switch step {
156→                case .verifyCurrent, .createNew, .confirmNew:
157→                    Spacer()
158→                    patternInputSection
159→                    Spacer()
160→
161→                    // Validation feedback — fixed height to prevent layout shift
162→                    Group {
163→                        if let result = validationResult, step == .createNew {
164→                            PatternValidationFeedbackView(result: result)
165→                                .accessibilityIdentifier("change_pattern_validation_feedback")
166→                        } else if let error = errorMessage {
167→                            HStack {
168→                                Image(systemName: "xmark.circle.fill")
169→                                    .foregroundStyle(.vaultHighlight)
170→                                Text(error)
171→                                    .font(.caption)
172→                            }
173→                            .padding()
174→                            .frame(maxWidth: .infinity, alignment: .leading)
175→                            .vaultGlassBackground(cornerRadius: 12)
176→                            .transition(.scale.combined(with: .opacity))
177→                            .accessibilityIdentifier("change_pattern_error_message")
178→                        } else {
179→                            Color.clear
180→                        }
181→                    }
182→                    .frame(minHeight: 80)
183→
184→                case .complete:
185→                    ScrollView {
186→                        completeSection
187→                            .padding(.vertical, 8)
188→                    }
189→                    .scrollIndicators(.hidden)
190→                }
191→
192→                // Bottom buttons
193→                bottomButtons
194→            }
195→            .padding()
196→            .background(Color.vaultBackground.ignoresSafeArea())
197→            .navigationTitle("Change Pattern")
198→            .navigationBarTitleDisplayMode(.inline)
199→            .toolbar {
200→                ToolbarItem(placement: .cancellationAction) {
201→                    Button("Cancel") { dismiss() }
202→                }
203→            }
204→            .alert("Save New Pattern?", isPresented: $showSaveConfirmation) {
205→                Button("Review", role: .cancel) { }
206→                Button("Save", role: .destructive) {
207→                    completePatternChange()
208→                }
209→            } message: {
210→                if isBackupEnabled {
211→                    Text("Your current pattern will no longer work. Make sure you've written down your new recovery phrase.\n\nYour iCloud backup will be automatically updated with your new pattern.")
212→                } else {
213→                    Text("Your current pattern will no longer work. Make sure you've written down your new recovery phrase.")
214→                }
215→            }
216→            .onAppear {
217→                if skipVerification {
218→                    changePatternLogger.debug("Recovery unlock detected — skipping pattern verification")
219→                    flow.skipVerification()
220→                    patternState.reset()
221→                }
222→            }
223→        }
224→    }
225→
226→    // MARK: - Subviews
227→
228→    @ViewBuilder
229→    private var patternInputSection: some View {
230→        PatternGridView(
231→            state: patternState,
232→            showFeedback: .constant(step != .verifyCurrent),
233→            onPatternComplete: handlePatternComplete
234→        )
235→        .frame(width: 280, height: 280)
236→        .disabled(flow.isProcessing)
237→        .opacity(flow.isProcessing ? 0.6 : 1)
238→        .accessibilityIdentifier("change_pattern_grid")
239→    }
240→
241→    private var completeSection: some View {
242→        VStack(spacing: 20) {
243→            Image(systemName: "checkmark.shield.fill")
244→                .font(.system(size: 48))
245→                .foregroundStyle(.green)
246→
247→            Text("Pattern Changed")
248→                .font(.title2)
249→                .fontWeight(.bold)
250→
251→            Text("Your vault is now protected by your new pattern. Your recovery phrase has been regenerated.")
252→                .font(.subheadline)
253→                .foregroundStyle(.vaultSecondaryText)
254→                .multilineTextAlignment(.center)
255→                .padding(.horizontal)
256→
257→            // Recovery phrase display
258→            if !flow.newRecoveryPhrase.isEmpty {
259→                PhraseDisplayCard(phrase: flow.newRecoveryPhrase)
260→                    .padding(.horizontal)
261→            }
262→
263→            Text("Write this down now. If you forget your pattern, this is the only way to recover your vault.")
264→                .font(.caption)
265→                .foregroundStyle(.vaultHighlight)
266→                .multilineTextAlignment(.center)
267→                .padding(.horizontal)
268→        }
269→    }
270→
271→    @ViewBuilder
272→    private var bottomButtons: some View {
273→        Group {
274→            if step == .complete {
275→                Button("Done") {
276→                    dismiss()
277→                }
278→                .buttonStyle(.borderedProminent)
279→                .controlSize(.large)
280→                .accessibilityIdentifier("change_pattern_done")
281→            } else if step == .createNew {
282→                // Placeholder for layout consistency
283→                Color.clear
284→                    .frame(height: 50)
285→            } else {
286→                HStack(spacing: 16) {
287→                    if step == .confirmNew {
288→                        Button("Try Again") {
289→                            flow.resetForStartOver()
290→                            patternState.reset()
291→                        }
292→                        .accessibilityIdentifier("change_pattern_try_again")
293→                    }
294→
295→                    Color.clear
296→                }
297→                .frame(height: 50)
298→            }
299→        }
300→    }
301→
302→    @ViewBuilder
303→    private var maestroChangePatternTestHooks: some View {
304→        #if DEBUG
305→        // Only visible in DEBUG builds when specific launch args are present
306→        if ProcessInfo.processInfo.arguments.contains("MAESTRO_CHANGE_PATTERN_TEST") {
307→            VStack {
308→                Button("[TEST] Skip Verify") {
309→                    if let testPattern = appState.currentPattern {
310→                        flow.skipVerifyForTesting(pattern: testPattern)
311→                        patternState.reset()
312→                    }
313→                }
314→                .font(.caption)
315→                .foregroundStyle(.vaultSecondaryText)
316→                .accessibilityIdentifier("maestro_change_pattern_skip_verify")
317→            }
318→        }
319→        #endif
320→    }
321→
322→    // MARK: - Computed Properties
323→
324→    private var skipVerification: Bool {
325→        appState.currentPattern == nil
326→    }
327→
328→    private var step: ChangePatternStep { flow.step }
329→    private var totalSteps: Int { skipVerification ? 2 : 3 }
330→    private var stepIndex: Int {
331→        switch step {
332→        case .verifyCurrent: return 0
333→        case .createNew: return 0
334→        case .confirmNew: return 1
335→        case .complete: return 2
336→        }
337→    }
338→
339→    private var stepTitle: String {
340→        switch step {
341→        case .verifyCurrent:
342→            return "Verify Current Pattern"
343→        case .createNew:
344→            return "Create New Pattern"
345→        case .confirmNew:
346→            return "Confirm Pattern"
347→        case .complete:
348→            return "Pattern Changed"
349→        }
350→    }
351→
352→    private var stepSubtitle: String {
353→        switch step {
354→        case .verifyCurrent:
355→            return "Enter your current pattern to begin"
356→        case .createNew:
357→            return "Draw a new pattern with at least 6 dots"
358→        case .confirmNew:
359→            return "Draw the same pattern again to confirm"
360→        case .complete:
361→            return "Your vault is now protected by your new pattern"
362→        }
363→    }
364→
365→    private var validationResult: PatternValidationResult? { flow.validationResult }
366→    private var errorMessage: String? { flow.errorMessage }
367→
368→    private var isBackupEnabled: Bool {
369→        UserDefaults.standard.bool(forKey: "iCloudBackupEnabled")
370→    }
371→
372→    private var isMaestroHookEnabled: Bool {
373→        #if DEBUG
374→        return ProcessInfo.processInfo.arguments.contains("MAESTRO_CHANGE_PATTERN_TEST")
375→        #else
376→        return false
377→        #endif
378→    }
379→
380→    // MARK: - Logic
381→
382→    private func handlePatternComplete(_ pattern: [Int]) {
383→        switch step {
384→        case .verifyCurrent:
385→            verifyCurrentPattern(pattern)
386→
387→        case .createNew:
388→            validateNewPattern(pattern)
389→
390→        case .confirmNew:
391→            confirmNewPattern(pattern)
392→
393→        case .complete:
394→            break
395→        }
396→    }
397→
398→    private func verifyCurrentPattern(_ pattern: [Int]) {
399→        guard let currentKey = appState.currentVaultKey else {
400→            flow.showError("No vault key available")
401→            patternState.reset()
402→            return
403→        }
404→
405→        // Validate pattern structure first
406→        let validation = PatternValidator.shared.validate(pattern, gridSize: patternState.gridSize)
407→        if !validation.isValid {
408→            flow.showError(validation.errors.first?.message ?? "Invalid pattern")
409→            patternState.reset()
410→            return
411→        }
412→
413→        guard flow.beginProcessingIfIdle() else { return }
414→
415→        Task {
416→            do {
417→                // Derive key from entered pattern and check if it matches current key
418→                let enteredKey = try await KeyDerivation.deriveKey(from: pattern, gridSize: patternState.gridSize)
419→
420→                await MainActor.run {
421→                    if enteredKey == currentKey.rawBytes {
422→                        // Pattern verified - move to next step
423→                        changePatternLogger.debug("Current pattern verified")
424→                        flow.transitionToCreate(currentPattern: pattern)
425→                        patternState.reset()
426→                    } else {
427→                        // Pattern doesn't match
428→                        changePatternLogger.debug("Current pattern incorrect")
429→                        flow.showError("Incorrect pattern. Please try again.")
430→                        patternState.reset()
431→                    }
432→                    flow.endProcessing()
433→                }
434→            } catch {
435→                await MainActor.run {
436→                    flow.showError("Error verifying pattern: \(error.localizedDescription)")
437→                    patternState.reset()
438→                    flow.endProcessing()
439→                }
440→            }
441→        }
442→    }
443→
444→    private func validateNewPattern(_ pattern: [Int]) {
445→        guard flow.beginProcessingIfIdle() else { return }
446→
447→        Task {
448→            // First, validate the pattern structure
449→            let result = PatternValidator.shared.validate(pattern, gridSize: patternState.gridSize)
450→
451→            if result.isValid {
452→                // Pattern valid — don't show feedback yet (avoids brief flash before transition)
453→                do {
454→                    let newKey = try await KeyDerivation.deriveKey(from: pattern, gridSize: patternState.gridSize)
455→                    let hasFiles = VaultStorage.shared.vaultHasFiles(for: VaultKey(newKey))
456→
457→                    await MainActor.run {
458→                        if hasFiles {
459→                            changePatternLogger.info("Pattern already used by a vault with files")
