     1import SwiftUI
     2import QuickLook
     3
     4struct SecureImageViewer: View {
     5    let file: VaultFileItem
     6    let vaultKey: VaultKey?
     7    var onDelete: ((UUID) -> Void)? = nil
     8    var allowDownloads: Bool = true
     9
    10    @Environment(\.dismiss) private var dismiss
    11    @State private var image: UIImage?
    12    @State private var tempFileURL: URL?
    13    @State private var isLoading = true
    14    @State private var error: String?
    15    @State private var showingExportConfirmation = false
    16    @State private var showingDeleteConfirmation = false
    17    @State private var showingActions = false
    18    @State private var shareURL: URL?
    19
    20    private var isImage: Bool { (file.mimeType ?? "").hasPrefix("image/") }
    21
    22    var body: some View {
    23        VStack(spacing: 0) {
    24            HStack {
    25                Button("Done") {
    26                    dismiss()
    27                }
    28                Spacer()
    29                Button(action: { showingActions = true }) {
    30                    Image(systemName: "ellipsis.circle")
    31                        .imageScale(.large)
    32                }
    33            }
    34            .padding(.horizontal)
    35            .padding(.vertical, 12)
    36            .background(Color.vaultBackground)
    37            Divider()
    38
    39            ZStack {
    40                Color.vaultBackground.ignoresSafeArea()
    41
    42                if isLoading {
    43                    ProgressView()
    44                } else if let image = image {
    45                    imageView(image)
    46                } else if let url = tempFileURL {
    47                    QuickLookPreview(url: url)
    48                        .ignoresSafeArea(edges: .bottom)
    49                } else if let error = error {
    50                    errorView(error)
    51                }
    52            }
    53        }
    54        .task { loadFile() }
    55        .onDisappear(perform: cleanup)
    56        .alert("Export File?", isPresented: $showingExportConfirmation) {
    57            Button("Export", role: .destructive) { exportFile() }
    58            Button("Cancel", role: .cancel) { /* No-op */ }
    59        } message: {
    60            Text("This will save the file outside the vault. It will no longer be protected.")
    61        }
    62        .alert("Delete File?", isPresented: $showingDeleteConfirmation) {
    63            Button("Delete", role: .destructive) { deleteFile() }
    64            Button("Cancel", role: .cancel) { /* No-op */ }
    65        } message: {
    66            Text("This file will be permanently deleted from the vault.")
    67        }
    68        .confirmationDialog("Actions", isPresented: $showingActions, titleVisibility: .visible) {
    69            if allowDownloads {
    70                Button("Export") { showingExportConfirmation = true }
    71            }
    72            if onDelete != nil {
    73                Button("Delete", role: .destructive) { showingDeleteConfirmation = true }
    74            }
    75            Button("Cancel", role: .cancel) { /* No-op */ }
    76        }
    77        .onChange(of: shareURL) { _, url in
    78            guard let url else { return }
    79            ShareSheetHelper.present(items: [url]) {
    80                FileUtilities.cleanupTemporaryFile(at: url)
    81                self.shareURL = nil
    82            }
    83        }
    84    }
    85
    86    // MARK: - Image View
    87
    88    private func imageView(_ image: UIImage) -> some View {
    89        GeometryReader { geometry in
    90            ScrollView([.horizontal, .vertical]) {
    91                Image(uiImage: image)
    92                    .resizable()
    93                    .scaledToFit()
    94                    .frame(
    95                        width: geometry.size.width,
    96                        height: geometry.size.height
    97                    )
    98            }
    99            .scrollIndicators(.hidden)
   100        }
   101    }
   102
   103    // MARK: - Error View
   104
   105    private func errorView(_ message: String) -> some View {
   106        VStack(spacing: 16) {
   107            Image(systemName: "exclamationmark.triangle")
   108                .font(.largeTitle)
   109                .foregroundStyle(.yellow)
   110
   111            Text("Unable to load file")
   112                .font(.headline)
   113
   114            Text(message)
   115                .font(.subheadline)
   116                .foregroundStyle(.vaultSecondaryText)
   117        }
   118    }
   119
   120    // MARK: - Actions
   121
   122    private func loadFile() {
   123        guard let key = vaultKey else {
   124            error = "No vault key"
   125            isLoading = false
   126            return
   127        }
   128
   129        Task {
   130            do {
   131                let isImage = (file.mimeType ?? "").hasPrefix("image/")
   132
   133                if isImage {
   134                    // Images: load into memory for display
   135                    let (_, content) = try VaultStorage.shared.retrieveFile(id: file.id, with: key)
   136                    if let uiImage = UIImage(data: content) {
   137                        await MainActor.run {
   138                            self.image = uiImage
   139                            self.isLoading = false
   140                        }
   141                        return
   142                    }
   143                }
   144
   145                // Non-images (or failed image decode): stream-decrypt to temp file
   146                let (_, decryptedURL) = try VaultStorage.shared.retrieveFileToTempURL(id: file.id, with: key)
   147                let filename = file.filename ?? "file_\(file.id.uuidString)"
   148                let tempDir = FileManager.default.temporaryDirectory
   149                    .appendingPathComponent("vault_preview", isDirectory: true)
   150                try FileManager.default.createDirectory(at: tempDir, withIntermediateDirectories: true)
   151                let tempURL = tempDir.appendingPathComponent(filename)
   152                try FileManager.default.moveItem(at: decryptedURL, to: tempURL)
   153
   154                await MainActor.run {
   155                    self.tempFileURL = tempURL
   156                    self.isLoading = false
   157                }
   158            } catch {
   159                await MainActor.run {
   160                    self.error = "Failed to decrypt file"
   161                    self.isLoading = false
   162                }
   163            }
   164        }
   165    }
   166
   167    private func cleanup() {
   168        image = nil
   169        FileUtilities.cleanupTemporaryFile(at: tempFileURL)
   170        tempFileURL = nil
   171    }
   172
   173    private func exportFile() {
   174        guard let key = vaultKey else { return }
   175
   176        Task {
   177            do {
   178                // Use streaming decryption to avoid 2x memory peak for large files
   179                let (_, decryptedURL) = try VaultStorage.shared.retrieveFileToTempURL(id: file.id, with: key)
   180                let filename = file.filename ?? "Export_\(file.id.uuidString)"
   181                let tempDir = URL(fileURLWithPath: NSTemporaryDirectory(), isDirectory: true)
   182                let url = tempDir.appendingPathComponent(filename)
   183                try FileManager.default.moveItem(at: decryptedURL, to: url)
   184                await MainActor.run {
   185                    shareURL = url
   186                }
   187            } catch {
   188                // Ignore export errors
   189            }
   190        }
   191    }
   192
   193    private func deleteFile() {
   194        guard let key = vaultKey else {
   195            onDelete?(file.id)
   196            dismiss()
   197            return
   198        }
   199
   200        Task {
   201            do {
   202                try VaultStorage.shared.deleteFile(id: file.id, with: key)
   203            } catch {
   204                EmbraceManager.shared.captureError(error)
   205            }
   206            await MainActor.run {
   207                onDelete?(file.id)
   208                dismiss()
   209            }
   210        }
   211    }
   212}
