     8private let vmLogger = Logger(subsystem: "app.vaultaire.ios", category: "VaultViewModel")
     9
    10@MainActor
    11@Observable
    12final class VaultViewModel {
    13
    14    // MARK: - Dependencies (set via configure())
    15
    16    private(set) weak var appState: AppState?
    17    private(set) weak var subscriptionManager: SubscriptionManager?
    18
    19    // MARK: - Core Data State
    20
    21    var files: [VaultFileItem] = []
    22    var masterKey: MasterKey?
    23    var isLoading = true
    24
    25    // MARK: - Import State
    26
    27    var importProgress: (completed: Int, total: Int)?
    28    var activeImportTask: Task<Void, Never>?
    29    var activeLoadTask: Task<Void, Never>?
    30    var isImportingPendingFiles = false
    31    var pendingImport: PendingImport?
    32
    33    // MARK: - Batch Edit State
    34
    35    var isEditing = false
    36    var selectedIds: Set<UUID> = []
    37    var isDeleteInProgress = false
    38    var showingBatchDeleteConfirmation = false
    39
    40    // MARK: - Export State
    41
    42    var exportURLs: [URL] = []
    43
    44    // MARK: - Shared Vault State
    45
    46    var isSharedVault = false
    47    var sharePolicy: VaultStorage.SharePolicy?
    48    var sharedVaultId: String?
    49    var updateAvailable = false
    50    var isUpdating = false
    51    var hasCountedOpenThisSession = false
    52    var selfDestructMessage: String?
    53    var showSelfDestructAlert = false
    54
    55    // MARK: - Filter / Sort State
    56
    57    var searchText = ""
    58    var sortOrder: SortOrder = .dateNewest
    59    var fileFilter: FileFilter = FileFilter(rawValue: UserDefaults.standard.string(forKey: "vaultFileFilter") ?? FileFilter.all.rawValue) ?? .all
    60
    61    // MARK: - Toast
    62
    63    var toastMessage: ToastMessage?
    64
    65    // MARK: - Transfer Manager
    66
    67    var transferManager = BackgroundShareTransferManager.shared
    68
    69    // MARK: - Visible Files (computed from observed properties)
    70
    71    // MARK: - Computed
    72
    73    var useDateGrouping: Bool {
    74        sortOrder == .dateNewest || sortOrder == .dateOldest
    75    }
    76
    77    var fileOptimization: String = UserDefaults.standard.string(forKey: "fileOptimization") ?? "optimized"
    78
    79    // MARK: - Init
    80
    81    func configure(appState: AppState, subscriptionManager: SubscriptionManager) {
    82        self.appState = appState
    83        self.subscriptionManager = subscriptionManager
    84    }
    85
    86    // MARK: - Visible Files
    87
    88    func computeVisibleFiles() -> VaultView.VisibleFiles {
    89        var visible = files
    90        switch fileFilter {
    91        case .all:
    92            break
    93        case .media:
    94            visible = visible.filter {
    95                let mime = $0.mimeType ?? ""
    96                return mime.hasPrefix("image/") || mime.hasPrefix("video/")
    97            }
    98        case .documents:
    99            visible = visible.filter {
   100                let mime = $0.mimeType ?? ""
   101                return !mime.hasPrefix("image/") && !mime.hasPrefix("video/")
   102            }
   103        }
   104        if !searchText.isEmpty {
   105            visible = visible.filter {
   106                ($0.filename ?? "").localizedStandardContains(searchText)
   107            }
   108        }
   109        switch sortOrder {
   110        case .dateNewest:
   111            visible.sort { ($0.createdAt ?? .distantPast) > ($1.createdAt ?? .distantPast) }
   112        case .dateOldest:
   113            visible.sort { ($0.createdAt ?? .distantPast) < ($1.createdAt ?? .distantPast) }
   114        case .sizeSmallest:
   115            visible.sort { $0.size < $1.size }
   116        case .sizeLargest:
   117            visible.sort { $0.size > $1.size }
   118        case .name:
   119            visible.sort { ($0.filename ?? "").localizedStandardCompare($1.filename ?? "") == .orderedAscending }
   120        }
   121
   122        let media = visible.filter { $0.isMedia }
   123        let documents = visible.filter { !$0.isMedia }
   124        let mediaIndexById = Dictionary(
   125            uniqueKeysWithValues: media.enumerated().map { ($1.id, $0) }
   126        )
   127        return VaultView.VisibleFiles(
   128            all: visible,
   129            media: media,
   130            documents: documents,
   131            mediaIndexById: mediaIndexById
   132        )
   133    }
   134
   135    /// Persists fileFilter to UserDefaults and updates the stored property.
   136    func setFileFilter(_ filter: FileFilter) {
   137        fileFilter = filter
   138        UserDefaults.standard.set(filter.rawValue, forKey: "vaultFileFilter")
   139    }
   140
   141    // MARK: - Load
   142
   143    func loadVault() {
   144        guard appState?.currentVaultKey != nil else {
   145            isLoading = false
   146            return
   147        }
   148        loadFiles()
   149        checkSharedVaultStatus()
   150    }
   151
   152    func loadFiles() {
   153        vmLogger.debug("loadFiles() called (current files.count=\(self.files.count), importActive=\(self.activeImportTask != nil))")
   154
   155        guard let key = appState?.currentVaultKey else {
   156            vmLogger.warning("loadFiles(): no vault key, aborting")
   157            isLoading = false
   158            return
   159        }
   160
   161        activeLoadTask?.cancel()
   162        activeLoadTask = Task.detached(priority: .userInitiated) {
   163            do {
   164                let result = try VaultStorage.shared.listFilesLightweight(with: key)
   165                for entry in result.files {
   166                    if let encThumb = entry.encryptedThumbnail {
   167                        await ThumbnailCache.shared.storeEncrypted(id: entry.fileId, data: encThumb)
   168                    }
   169                }
   170                let items = result.files.map { entry in
   171                    VaultFileItem(
   172                        id: entry.fileId,
   173                        size: entry.size,
   174                        hasThumbnail: entry.encryptedThumbnail != nil,
   175                        mimeType: entry.mimeType,
   176                        filename: entry.filename,
   177                        createdAt: entry.createdAt,
   178                        duration: entry.duration
   179                    )
   180                }
   181                await MainActor.run {
   182                    vmLogger.info("loadFiles() loaded \(items.count) files from disk (was \(self.files.count))")
   183                    self.masterKey = MasterKey(result.masterKey)
   184                    self.files = items
   185                    self.isLoading = false
   186
   187                    // Auto-detect best filter based on vault contents
   188                    if !items.isEmpty {
   189                        let hasNonMedia = items.contains { !$0.isMedia }
   190                        if !hasNonMedia && self.fileFilter == .all {
   191                            self.setFileFilter(.media)
   192                        } else if hasNonMedia && self.fileFilter == .media {
   193                            self.setFileFilter(.all)
   194                        }
   195                    }
   196
   197                    EmbraceManager.shared.addBreadcrumb(category: "vault.opened", data: ["fileCount": items.count])
   198                }
   199            } catch {
   200                vmLogger.error("loadFiles() failed: \(error.localizedDescription, privacy: .public)")
   201                await MainActor.run {
   202                    self.files = []
   203                    self.isLoading = false
   204                }
   205            }
   206        }
   207    }
   208
   209    // MARK: - Selection
   210
   211    func toggleSelection(_ id: UUID) {
   212        if selectedIds.contains(id) {
   213            selectedIds.remove(id)
   214        } else {
   215            selectedIds.insert(id)
   216        }
   217    }
   218
   219    // MARK: - Batch Operations
   220
   221    func batchDelete() {
   222        guard let key = appState?.currentVaultKey else { return }
   223        let idsToDelete = selectedIds
   224        let count = idsToDelete.count
   225
   226        isDeleteInProgress = true
   227        importProgress = (0, count)
   228        IdleTimerManager.shared.disable()
   229        isEditing = false
   230
   231        activeImportTask?.cancel()
   232        activeImportTask = Task.detached(priority: .userInitiated) {
   233            do {
   234                try VaultStorage.shared.deleteFiles(ids: idsToDelete, with: key) { deleted in
   235                    Task { @MainActor in
   236                        guard !Task.isCancelled else { return }
   237                        self.importProgress = (deleted, count)
   238                    }
   239                }
   240            } catch {
   241                await MainActor.run {
   242                    self.toastMessage = .error("Delete failed: \(error.localizedDescription)")
   243                }
   244            }
   245
   246            await MainActor.run {
   247                guard !Task.isCancelled else { return }
   248                self.files.removeAll { idsToDelete.contains($0.id) }
   249                self.selectedIds.removeAll()
   250                self.importProgress = nil
   251                self.isDeleteInProgress = false
   252                IdleTimerManager.shared.enable()
   253                self.toastMessage = .filesDeleted(count)
   254            }
   255
   256            if !Task.isCancelled {
   257                await MainActor.run {
   258                    ShareSyncManager.shared.scheduleSync(vaultKey: key)
   259                }
   260            }
   261        }
   262    }
   263
   264    func batchExport() {
   265        guard let key = appState?.currentVaultKey else { return }
   266        let idsToExport = selectedIds
   267        let filesList = files
   268
   269        Task.detached(priority: .userInitiated) {
   270            let tempDir = URL(fileURLWithPath: NSTemporaryDirectory(), isDirectory: true)
   271            var urls: [URL] = []
   272
   273            for id in idsToExport {
   274                guard !Task.isCancelled else { break }
   275                guard let result = try? VaultStorage.shared.retrieveFile(id: id, with: key) else { continue }
   276                let file = filesList.first { $0.id == id }
   277                let filename = file?.filename ?? "Export_\(id.uuidString)"
   278                let url = tempDir.appendingPathComponent(filename)
   279                try? result.content.write(to: url, options: [.atomic])
   280                urls.append(url)
   281            }
   282
   283            let finalizedURLs = urls
   284            await MainActor.run { [finalizedURLs] in
   285                guard !Task.isCancelled else { return }
   286                self.exportURLs = finalizedURLs
   287            }
   288        }
   289    }
   290
   291    func cleanupExportFiles() {
   292        for url in exportURLs {
   293            FileUtilities.cleanupTemporaryFile(at: url)
   294        }
   295        exportURLs = []
   296    }
   297
   298    func deleteFileById(_ id: UUID) {
   299        guard let key = appState?.currentVaultKey else { return }
   300        Task.detached(priority: .userInitiated) {
   301            do {
   302                try VaultStorage.shared.deleteFile(id: id, with: key)
   303                await MainActor.run {
   304                    if let idx = self.files.firstIndex(where: { $0.id == id }) {
   305                        self.files.remove(at: idx)
   306                    }
   307                    self.toastMessage = .filesDeleted(1)
   308                }
   309            } catch {
   310                await MainActor.run {
   311                    self.toastMessage = .error("Failed to delete file: \(error.localizedDescription)")
   312                }
   313            }
   314        }
   315    }
   316
   317    // MARK: - Import: Camera Capture
   318
   319    func handleCapturedImage(_ imageData: Data) {
   320        guard !isSharedVault, let key = appState?.currentVaultKey else { return }
   321        let currentMasterKey = self.masterKey
   322        let optimizationMode = MediaOptimizer.Mode(rawValue: fileOptimization) ?? .optimized
   323
   324        Task.detached(priority: .userInitiated) {
   325            do {
   326                let tempInputURL = FileManager.default.temporaryDirectory
   327                    .appendingPathComponent(UUID().uuidString)
   328                    .appendingPathExtension("jpg")
   329                try imageData.write(to: tempInputURL, options: [.atomic])
   330
   331                let (optimizedURL, mimeType, wasOptimized) = try await MediaOptimizer.shared.optimize(
   332                    fileURL: tempInputURL, mimeType: "image/jpeg", mode: optimizationMode
   333                )
   334                defer {
   335                    if wasOptimized { try? FileManager.default.removeItem(at: optimizedURL) }
   336                    try? FileManager.default.removeItem(at: tempInputURL)
   337                }
   338
   339                let ext = mimeType == "image/heic" ? "heic" : "jpg"
   340                let filename = "IMG_\(Date().timeIntervalSince1970).\(ext)"
   341                let thumbnail = FileUtilities.generateThumbnail(fromFileURL: optimizedURL)
   342
   343                let fileId = try VaultStorage.shared.storeFileFromURL(
   344                    optimizedURL, filename: filename, mimeType: mimeType,
   345                    with: key, thumbnailData: thumbnail
   346                )
   347                let encThumb = thumbnail.flatMap { try? CryptoEngine.encrypt($0, with: currentMasterKey?.rawBytes ?? key.rawBytes) }
   348                if let encThumb {
   349                    await ThumbnailCache.shared.storeEncrypted(id: fileId, data: encThumb)
   350                }
   351                await MainActor.run {
   352                    if let milestone = MilestoneTracker.shared.checkFirstFile(totalCount: self.files.count + 1) {
   353                        self.toastMessage = .milestone(milestone)
   354                    } else {
   355                        self.toastMessage = .fileEncrypted()
   356                    }
   357                    self.loadFiles()
   358                }
   359
   360                await ShareSyncManager.shared.scheduleSync(vaultKey: key)
   361            } catch {
   362                await MainActor.run {
   363                    self.toastMessage = .importFailed(1, imported: 0, reason: error.localizedDescription)
   364                }
   365            }
   366        }
   367    }
   368
   369    // MARK: - Import: Photo Picker
   370
   371    /// Returns limit info (remaining, selected) if the user hit the free plan cap, nil if import proceeds normally.
   372    func handleSelectedPhotos(_ results: [PHPickerResult]) -> (remaining: Int, selected: Int)? {
   373        guard !isSharedVault, appState?.currentVaultKey != nil else { return nil }
   374        guard let subscriptionManager else { return nil }
   375
   376        if !subscriptionManager.isPremium {
   377            let remaining = max(0, SubscriptionManager.maxFreeFilesPerVault - files.count)
   378            if remaining == 0 {
   379                return (0, results.count)
   380            }
   381            if results.count > remaining {
   382                pendingImport = .photos(results)
   383                return (remaining, results.count)
   384            }
   385        }
   386
   387        performPhotoImport(results)
   388        return nil
   389    }
   390
   391    func performPhotoImport(_ results: [PHPickerResult]) {
   392        guard let key = appState?.currentVaultKey else { return }
   393        let encryptionKey = self.masterKey?.rawBytes ?? key.rawBytes
   394        let count = results.count
   395        let optimizationMode = MediaOptimizer.Mode(rawValue: fileOptimization) ?? .optimized
   396
   397        activeImportTask?.cancel()
   398        importProgress = (0, count)
   399        IdleTimerManager.shared.disable()
   400
   401        activeImportTask = Task.detached(priority: .userInitiated) {
   402            var successCount = 0
   403            var failedCount = 0
   404            var lastErrorReason: String?
   405            for (index, result) in results.enumerated() {
   406                guard !Task.isCancelled else { break }
   407
   408                let provider = result.itemProvider
   409                let isVideo = provider.hasItemConformingToTypeIdentifier(UTType.movie.identifier)
   410
   411                do {
   412                    if isVideo {
   413                        let tempVideoURL = try await VaultView.loadVideoURL(from: provider)
   414                        guard !Task.isCancelled else {
   415                            try? FileManager.default.removeItem(at: tempVideoURL)
   416                            break
   417                        }
   418
   419                        let ext = tempVideoURL.pathExtension.isEmpty ? "mov" : tempVideoURL.pathExtension
   420                        let mime = FileUtilities.mimeType(forExtension: ext)
   421                        let sourceMimeType = mime.hasPrefix("video/") ? mime : "video/quicktime"
   422
   423                        let (optimizedURL, mimeType, wasOptimized) = try await MediaOptimizer.shared.optimize(
   424                            fileURL: tempVideoURL, mimeType: sourceMimeType, mode: optimizationMode
   425                        )
   426                        defer {
   427                            if wasOptimized { try? FileManager.default.removeItem(at: optimizedURL) }
   428                            try? FileManager.default.removeItem(at: tempVideoURL)
   429                        }
   430
   431                        let baseFilename = provider.suggestedName.map { name -> String in
   432                            if (name as NSString).pathExtension.isEmpty { return name + ".\(ext)" }
   433                            return name
   434                        } ?? "VID_\(Date().timeIntervalSince1970)_\(index).\(ext)"
   435                        let filename = wasOptimized ? MediaOptimizer.updatedFilename(baseFilename, newMimeType: mimeType) : baseFilename
   436
   437                        let metadata = await VaultView.generateVideoMetadata(from: optimizedURL)
   438                        let fileSize = (try? FileManager.default.attributesOfItem(atPath: optimizedURL.path)[.size] as? Int) ?? 0
   439
   440                        let fileId = try VaultStorage.shared.storeFileFromURL(
   441                            optimizedURL, filename: filename, mimeType: mimeType,
   442                            with: key, thumbnailData: metadata.thumbnail, duration: metadata.duration
   443                        )
   444
   445                        let encThumb = metadata.thumbnail.flatMap { try? CryptoEngine.encrypt($0, with: encryptionKey) }
   446                        if let encThumb {
   447                            await ThumbnailCache.shared.storeEncrypted(id: fileId, data: encThumb)
   448                        }
   449                        await MainActor.run {
   450                            guard !Task.isCancelled else { return }
   451                            self.files.append(VaultFileItem(
   452                                id: fileId, size: fileSize,
   453                                hasThumbnail: encThumb != nil, mimeType: mimeType,
   454                                filename: filename, createdAt: Date(), duration: metadata.duration
   455                            ))
   456                            self.importProgress = (index + 1, count)
   457                        }
   458                        successCount += 1
   459                    } else {
   460                        guard provider.canLoadObject(ofClass: UIImage.self) else {
   461                            failedCount += 1
   462                            lastErrorReason = "Unsupported file format"
   463                            await MainActor.run { self.importProgress = (index + 1, count) }
   464                            continue
   465                        }
   466
   467                        let image: UIImage? = await withCheckedContinuation { continuation in
   468                            provider.loadObject(ofClass: UIImage.self) { object, _ in
   469                                continuation.resume(returning: object as? UIImage)
   470                            }
   471                        }
   472                        guard !Task.isCancelled else { break }
   473
   474                        guard let image else {
   475                            failedCount += 1
   476                            lastErrorReason = "Could not convert image"
   477                            await MainActor.run { self.importProgress = (index + 1, count) }
   478                            continue
   479                        }
   480
   481                        let tempInputURL = FileManager.default.temporaryDirectory
   482                            .appendingPathComponent(UUID().uuidString)
   483                            .appendingPathExtension("jpg")
   484                        guard let jpegData = image.jpegData(compressionQuality: 0.8) else {
   485                            failedCount += 1
   486                            lastErrorReason = "Could not convert image"
   487                            await MainActor.run { self.importProgress = (index + 1, count) }
   488                            continue
   489                        }
   490                        try jpegData.write(to: tempInputURL, options: [.atomic])
   491
   492                        let (optimizedURL, mimeType, wasOptimized) = try await MediaOptimizer.shared.optimize(
   493                            fileURL: tempInputURL, mimeType: "image/jpeg", mode: optimizationMode
   494                        )
   495                        defer {
   496                            if wasOptimized { try? FileManager.default.removeItem(at: optimizedURL) }
   497                            try? FileManager.default.removeItem(at: tempInputURL)
   498                        }
   499
   500                        let ext = mimeType == "image/heic" ? "heic" : "jpg"
   501                        let filename = "IMG_\(Date().timeIntervalSince1970)_\(index).\(ext)"
   502
   503                        let thumbnail = FileUtilities.generateThumbnail(fromFileURL: optimizedURL)
   504                        let fileSize = (try? FileManager.default.attributesOfItem(atPath: optimizedURL.path)[.size] as? Int) ?? 0
   505
   506                        guard !Task.isCancelled else { break }
   507
   508                        let fileId = try VaultStorage.shared.storeFileFromURL(
   509                            optimizedURL, filename: filename, mimeType: mimeType,
   510                            with: key, thumbnailData: thumbnail
   511                        )
   512                        let encThumb = thumbnail.flatMap { try? CryptoEngine.encrypt($0, with: encryptionKey) }
   513                        if let encThumb {
   514                            await ThumbnailCache.shared.storeEncrypted(id: fileId, data: encThumb)
   515                        }
   516                        await MainActor.run {
   517                            guard !Task.isCancelled else { return }
   518                            self.files.append(VaultFileItem(
   519                                id: fileId, size: fileSize,
   520                                hasThumbnail: encThumb != nil, mimeType: mimeType,
   521                                filename: filename, createdAt: Date()
   522                            ))
   523                            self.importProgress = (index + 1, count)
   524                        }
   525                        successCount += 1
   526                    }
   527                } catch {
   528                    if Task.isCancelled { break }
   529                    failedCount += 1
   530                    lastErrorReason = error.localizedDescription
   531                    EmbraceManager.shared.addBreadcrumb(
   532                        category: "import.failed",
   533                        data: ["index": index, "isVideo": isVideo, "error": "\(error)"]
   534                    )
   535                    await MainActor.run { self.importProgress = (index + 1, count) }
   536                    #if DEBUG
   537                    print(" [VaultViewModel] Failed to import item \(index): \(error)")
   538                    #endif
   539                }
   540            }
   541
   542            let imported = successCount
   543            let failed = failedCount
   544            let errorReason = lastErrorReason
   545            await MainActor.run {
   546                guard !Task.isCancelled else {
   547                    vmLogger.warning("Photo import task cancelled before completion (imported=\(imported), failed=\(failed))")
   548                    return
   549                }
   550                self.importProgress = nil
   551                IdleTimerManager.shared.enable()
   552                if failed > 0 && imported == 0 {
   553                    self.toastMessage = .importFailed(failed, imported: 0, reason: errorReason)
   554                } else if failed > 0 {
   555                    self.toastMessage = .importFailed(failed, imported: imported, reason: errorReason)
   556                } else if let milestone = MilestoneTracker.shared.checkFirstFile(totalCount: self.files.count) {
   557                    self.toastMessage = .milestone(milestone)
   558                } else {
   559                    self.toastMessage = .filesImported(imported)
   560                }
   561                vmLogger.info("Photo import complete: imported=\(imported), failed=\(failed), files.count=\(self.files.count)")
   562                // Safety net: reload from disk to ensure in-memory state matches persisted state.
   563                // Guards against edge cases where files were stored but in-memory array diverged.
   564                if imported > 0 {
   565                    self.loadFiles()
   566                }
   567            }
   568
   569            if !Task.isCancelled {
   570                await MainActor.run {
   571                    ShareSyncManager.shared.scheduleSync(vaultKey: key)
   572                }
   573            }
   574        }
   575    }
   576
   577    // MARK: - Import: File Importer
   578
   579    /// Returns limit info (remaining, selected) if the user hit the free plan cap, nil if import proceeds normally.
   580    func handleImportedFiles(_ result: Result<[URL], Error>) -> (remaining: Int, selected: Int)? {
   581        guard !isSharedVault, appState?.currentVaultKey != nil else { return nil }
   582        guard case .success(let urls) = result else { return nil }
   583        guard let subscriptionManager else { return nil }
   584
   585        if !subscriptionManager.isPremium {
   586            let remaining = max(0, SubscriptionManager.maxFreeFilesPerVault - files.count)
   587            if remaining == 0 {
   588                return (0, urls.count)
   589            }
   590            if urls.count > remaining {
   591                pendingImport = .files(urls)
   592                return (remaining, urls.count)
   593            }
   594        }
   595
   596        performFileImport(urls)
   597        return nil
   598    }
   599
   600    func performFileImport(_ urls: [URL]) {
   601        guard let key = appState?.currentVaultKey else { return }
   602        let encryptionKey = self.masterKey?.rawBytes ?? key.rawBytes
   603        let count = urls.count
   604        let showProgress = count > 1
   605        let optimizationMode = MediaOptimizer.Mode(rawValue: fileOptimization) ?? .optimized
   606
   607        activeImportTask?.cancel()
   608        if showProgress {
   609            importProgress = (0, count)
   610        }
   611        IdleTimerManager.shared.disable()
   612
   613        activeImportTask = Task.detached(priority: .userInitiated) {
   614            var successCount = 0
   615            var failedCount = 0
   616            var lastErrorReason: String?
   617            for (index, url) in urls.enumerated() {
   618                guard !Task.isCancelled else { break }
   619                guard url.startAccessingSecurityScopedResource() else {
   620                    failedCount += 1
   621                    continue
   622                }
   623                defer { url.stopAccessingSecurityScopedResource() }
   624
   625                let originalFilename = url.lastPathComponent
   626                let sourceMimeType = FileUtilities.mimeType(forExtension: url.pathExtension)
   627
   628                do {
   629                    let (optimizedURL, mimeType, wasOptimized) = try await MediaOptimizer.shared.optimize(
   630                        fileURL: url, mimeType: sourceMimeType, mode: optimizationMode
   631                    )
   632                    defer {
   633                        if wasOptimized { try? FileManager.default.removeItem(at: optimizedURL) }
   634                    }
   635
   636                    let filename = wasOptimized
   637                        ? MediaOptimizer.updatedFilename(originalFilename, newMimeType: mimeType)
   638                        : originalFilename
   639
   640                    if mimeType.hasPrefix("video/") {
   641                        let metadata = await VaultView.generateVideoMetadata(from: optimizedURL)
   642                        let fileSize = (try? FileManager.default.attributesOfItem(atPath: optimizedURL.path)[.size] as? Int) ?? 0
   643
   644                        let fileId = try VaultStorage.shared.storeFileFromURL(
   645                            optimizedURL, filename: filename, mimeType: mimeType,
   646                            with: key, thumbnailData: metadata.thumbnail, duration: metadata.duration
   647                        )
   648                        let encThumb = metadata.thumbnail.flatMap { try? CryptoEngine.encrypt($0, with: encryptionKey) }
   649                        if let encThumb {
   650                            await ThumbnailCache.shared.storeEncrypted(id: fileId, data: encThumb)
   651                        }
   652                        await MainActor.run {
   653                            guard !Task.isCancelled else { return }
   654                            self.files.append(VaultFileItem(
   655                                id: fileId, size: fileSize,
   656                                hasThumbnail: encThumb != nil, mimeType: mimeType,
   657                                filename: filename, createdAt: Date(), duration: metadata.duration
   658                            ))
   659                            if showProgress { self.importProgress = (index + 1, count) }
   660                        }
   661                    } else {
   662                        let fileSize = (try? FileManager.default.attributesOfItem(atPath: optimizedURL.path)[.size] as? Int) ?? 0
   663                        let thumbnail = mimeType.hasPrefix("image/")
   664                            ? FileUtilities.generateThumbnail(fromFileURL: optimizedURL)
   665                            : nil
   666
   667                        let fileId = try VaultStorage.shared.storeFileFromURL(
   668                            optimizedURL, filename: filename, mimeType: mimeType,
   669                            with: key, thumbnailData: thumbnail
   670                        )
   671                        let encThumb = thumbnail.flatMap { try? CryptoEngine.encrypt($0, with: encryptionKey) }
   672                        if let encThumb {
   673                            await ThumbnailCache.shared.storeEncrypted(id: fileId, data: encThumb)
   674                        }
   675                        await MainActor.run {
   676                            guard !Task.isCancelled else { return }
   677                            self.files.append(VaultFileItem(
   678                                id: fileId, size: fileSize,
   679                                hasThumbnail: encThumb != nil, mimeType: mimeType,
   680                                filename: filename, createdAt: Date()
   681                            ))
   682                            if showProgress { self.importProgress = (index + 1, count) }
   683                        }
   684                    }
   685                    successCount += 1
   686                } catch {
   687                    if Task.isCancelled { break }
   688                    failedCount += 1
   689                    lastErrorReason = error.localizedDescription
   690                    if showProgress {
   691                        await MainActor.run { self.importProgress = (index + 1, count) }
   692                    }
   693                }
   694            }
   695
   696            let imported = successCount
   697            let failed = failedCount
   698            let errorReason = lastErrorReason
   699            await MainActor.run {
   700                guard !Task.isCancelled else {
   701                    vmLogger.warning("File import task cancelled before completion (imported=\(imported), failed=\(failed))")
   702                    return
   703                }
   704                self.importProgress = nil
   705                IdleTimerManager.shared.enable()
   706                if failed > 0 && imported == 0 {
   707                    self.toastMessage = .importFailed(failed, imported: 0, reason: errorReason)
   708                } else if failed > 0 {
   709                    self.toastMessage = .importFailed(failed, imported: imported, reason: errorReason)
   710                } else {
   711                    self.toastMessage = .filesImported(imported)
   712                }
   713                vmLogger.info("File import complete: imported=\(imported), failed=\(failed), files.count=\(self.files.count)")
   714                if imported > 0 {
   715                    self.loadFiles()
   716                }
   717            }
   718
   719            if !Task.isCancelled {
   720                await MainActor.run {
   721                    ShareSyncManager.shared.scheduleSync(vaultKey: key)
   722                }
   723            }
   724        }
   725    }
