   727    // MARK: - Import: Pending Files
   728
   729    func importPendingFiles() {
   730        guard !isImportingPendingFiles else { return }
   731        guard let vaultKey = appState?.currentVaultKey else { return }
   732
   733        isImportingPendingFiles = true
   734        IdleTimerManager.shared.disable()
   735
   736        let fingerprint = KeyDerivation.keyFingerprint(from: vaultKey.rawBytes)
   737        let initialPendingCount = StagedImportManager.pendingImportableFileCount(for: fingerprint)
   738        if initialPendingCount > 0 {
   739            importProgress = (0, initialPendingCount)
   740        }
   741
   742        activeImportTask?.cancel()
   743        activeImportTask = Task.detached(priority: .userInitiated) {
   744            let result = await ImportIngestor.processPendingImports(for: vaultKey) { progress in
   745                await MainActor.run {
   746                    guard !Task.isCancelled else { return }
   747                    self.importProgress = (progress.completed, progress.total)
   748                }
   749            }
   750
   751            await MainActor.run {
   752                self.isImportingPendingFiles = false
   753                self.importProgress = nil
   754                IdleTimerManager.shared.enable()
   755            }
   756
   757            guard !Task.isCancelled else { return }
   758
   759            let remainingImportable = StagedImportManager.pendingImportableFileCount(for: fingerprint)
   760            await MainActor.run {
   761                guard let appState = self.appState else { return }
   762                appState.hasPendingImports = remainingImportable > 0
   763                appState.pendingImportCount = remainingImportable
   764
   765                if result.imported > 0 {
   766                    self.loadFiles()
   767                    let generator = UINotificationFeedbackGenerator()
   768                    generator.notificationOccurred(.success)
   769                    if result.failed > 0 {
   770                        self.toastMessage = .importFailed(result.failed, imported: result.imported, reason: result.failureReason)
   771                    } else {
   772                        self.toastMessage = .filesImported(result.imported)
   773                    }
   774                } else if result.failed > 0 {
   775                    self.toastMessage = .importFailed(result.failed, imported: 0, reason: result.failureReason)
   776                    let generator = UINotificationFeedbackGenerator()
   777                    generator.notificationOccurred(.error)
   778                } else {
   779                    appState.hasPendingImports = false
   780                    appState.pendingImportCount = 0
   781                }
   782            }
   783        }
   784    }
   785
   786    func proceedWithLimitedImport(limitAlertRemaining: Int) {
   787        guard let pending = pendingImport else { return }
   788        let remaining = limitAlertRemaining
   789        pendingImport = nil
   790
   791        switch pending {
   792        case .photos(let results):
   793            performPhotoImport(Array(results.prefix(remaining)))
   794        case .files(let urls):
   795            performFileImport(Array(urls.prefix(remaining)))
   796        }
   797    }
   798
   799    // MARK: - Shared Vault
   800
   801    func checkSharedVaultStatus() {
   802        guard let key = appState?.currentVaultKey else { return }
   803
   804        Task {
   805            do {
   806                var index = try VaultStorage.shared.loadIndex(with: key)
   807
   808                let shared = index.isSharedVault ?? false
   809                await MainActor.run {
   810                    isSharedVault = shared
   811                    sharePolicy = index.sharePolicy
   812                    sharedVaultId = index.sharedVaultId
   813                }
   814
   815                guard shared else { return }
   816
   817                if let expires = index.sharePolicy?.expiresAt, Date() > expires {
   818                    await MainActor.run {
   819                        selfDestructMessage = "This shared vault has expired. The vault owner set an expiration date of \(expires.formatted(date: .abbreviated, time: .omitted)). All shared files have been removed."
   820                        showSelfDestructAlert = true
   821                    }
   822                    return
   823                }
   824
   825                if !hasCountedOpenThisSession {
   826                    let currentOpens = (index.openCount ?? 0) + 1
   827                    if let maxOpens = index.sharePolicy?.maxOpens, currentOpens > maxOpens {
   828                        await MainActor.run {
   829                            selfDestructMessage = "This shared vault has reached its maximum number of opens. All shared files have been removed."
   830                            showSelfDestructAlert = true
   831                        }
   832                        return
   833                    }
   834
   835                    index.openCount = currentOpens
   836                    try VaultStorage.shared.saveIndex(index, with: key)
   837                    await MainActor.run { hasCountedOpenThisSession = true }
   838                }
   839
   840                if let vaultId = index.sharedVaultId {
   841                    do {
   842                        let currentVersion = index.sharedVaultVersion ?? 1
   843                        if let _ = try await CloudKitSharingManager.shared.checkForUpdates(
   844                            shareVaultId: vaultId, currentVersion: currentVersion
   845                        ) {
   846                            await MainActor.run {
   847                                updateAvailable = true
   848                            }
   849                        }
   850                    } catch CloudKitSharingError.revoked {
   851                        await MainActor.run {
   852                            selfDestructMessage = "The vault owner has revoked your access to this shared vault. All shared files have been removed."
   853                            showSelfDestructAlert = true
   854                        }
   855                    } catch {
   856                        #if DEBUG
   857                        print(" [VaultViewModel] Failed to check for updates: \(error)")
   858                        #endif
   859                    }
   860                }
   861            } catch {
   862                #if DEBUG
   863                print(" [VaultViewModel] Failed to check shared vault status: \(error)")
   864                #endif
   865            }
   866        }
   867    }
   868
   869    func selfDestruct() {
   870        guard let key = appState?.currentVaultKey else { return }
   871
   872        do {
   873            let index = try VaultStorage.shared.loadIndex(with: key)
   874
   875            if let vaultId = index.sharedVaultId {
   876                Task {
   877                    do {
   878                        try await CloudKitSharingManager.shared.markShareConsumed(shareVaultId: vaultId)
   879                    } catch {
   880                        vmLogger.error("Failed to mark share consumed during self-destruct: \(error.localizedDescription, privacy: .public)")
   881                        EmbraceManager.shared.captureError(error, context: ["action": "markShareConsumed", "vaultId": vaultId])
   882                    }
   883                }
   884            }
   885
   886            for file in index.files where !file.isDeleted {
   887                do {
   888                    try VaultStorage.shared.deleteFile(id: file.fileId, with: key)
   889                } catch {
   890                    vmLogger.error("Failed to delete file during self-destruct \(file.fileId, privacy: .public): \(error.localizedDescription, privacy: .public)")
   891                    EmbraceManager.shared.captureError(error, context: ["action": "deleteFile", "fileId": file.fileId])
   892                }
   893            }
   894            try VaultStorage.shared.deleteVaultIndex(for: key)
   895        } catch {
   896            vmLogger.error("Self-destruct error: \(error.localizedDescription, privacy: .public)")
   897            EmbraceManager.shared.captureError(error, context: ["action": "selfDestruct"])
   898        }
   899
   900        appState?.lockVault()
   901    }
   902
   903    // MARK: - Vault Key Change
   904
   905    func handleVaultKeyChange(oldKey: VaultKey?, newKey: VaultKey?) {
   906        if activeImportTask != nil {
   907            vmLogger.warning("Vault key changed during active import  cancelling import (files.count=\(self.files.count))")
   908        }
   909        activeImportTask?.cancel()
   910        activeImportTask = nil
   911        importProgress = nil
   912        isDeleteInProgress = false
   913        IdleTimerManager.shared.enable()
   914
   915        if oldKey != newKey {
   916            vmLogger.info("Vault key changed: clearing files (had \(self.files.count) files)")
   917            files = []
   918            masterKey = nil
   919            Task { await ThumbnailCache.shared.clear() }
   920            isLoading = false
   921            isSharedVault = false
   922        }
   923
   924        if newKey != nil {
   925            ShareUploadManager.shared.resumePendingUploadsIfNeeded(trigger: "vault_key_changed")
   926        }
   927    }
   928
   929    // MARK: - Filter Helpers
   930
   931    func updateFilterAfterImport() {
   932        let hasNonMedia = files.contains { !$0.isMedia }
   933        if hasNonMedia && fileFilter == .media {
   934            setFileFilter(.all)
   935        } else if !hasNonMedia && fileFilter != .media {
   936            setFileFilter(.media)
   937        }
   938    }
   939}
