   215                            dismiss()
   216                        } else {
   217                            withAnimation(.spring(response: 0.3, dampingFraction: 0.82)) {
   218                                verticalDismissOffset = 0
   219                            }
   220                        }
   221                    },
   222                    onZoomChange: { scale in
   223                        currentZoomScale = scale
   224                    }
   225                )
   226
   227                if isVideo {
   228                    Button {
   229                        showingVideoPlayer = file
   230                    } label: {
   231                        Image(systemName: "play.circle.fill")
   232                            .font(.system(size: 72))
