   237                    .accessibilityIdentifier("viewer_play_video")
   238                    .allowsHitTesting(!isZoomed)
   239                }
   240            }
   241        } else {
   242            ProgressView()
   243                .tint(.white)
   244                .containerRelativeFrame([.horizontal, .vertical])
   245                .task(id: file.id) {
   246                    await loadFullImage(for: file)
   247                }
   248        }
   249    }
   250
   251    // MARK: - Image Loading
   252
   253    private func loadFullImage(for file: VaultFileItem) async {
   254        guard images[file.id] == nil else { return }
   255
   256        let isVideo = (file.mimeType ?? "").hasPrefix("video/")
   257
   258        if isVideo {
   259            guard let masterKey = masterKey, file.hasThumbnail else { return }
   260            if let uiImage = await ThumbnailCache.shared.decryptAndCache(id: file.id, masterKey: masterKey.rawBytes) {
   261                images[file.id] = uiImage
   262            }
   263        } else {
   264            guard let key = vaultKey else { return }
   265            let fileId = file.id
   266            // Decrypt and decode off main thread, downsampled to screen resolution
   267            let screenScale = await UIScreen.main.scale
   268            let uiImage: UIImage? = await Task.detached(priority: .userInitiated) {
   269                do {
   270                    let (header, content) = try VaultStorage.shared.retrieveFile(id: fileId, with: key)
   271                    guard header.mimeType.hasPrefix("image/") else { return nil }
   272                    return Self.downsampledImage(from: content, maxPixelSize: 1920 * Int(screenScale))
   273                } catch {
   274                    return nil
   275                }
   276            }.value
   277            if let uiImage {
   278                images[file.id] = uiImage
   279            }
   280        }
   281    }
   282
   283    private func preloadAdjacent(around index: Int) {
   284        let range = max(0, index - 1)...min(files.count - 1, index + 1)
   285        for i in range {
   286            let file = files[i]
   287            if images[file.id] == nil {
   288                Task { await loadFullImage(for: file) }
   289            }
   290        }
   291    }
   292
   293    private func evictDistant(from index: Int) {
   294        for (i, file) in files.enumerated() {
   295            if abs(i - index) > 2 {
   296                images.removeValue(forKey: file.id)
   297            }
   298        }
   299    }
   300
   301    // MARK: - Image Decoding
   302
   303    /// Decodes image data with downsampling to avoid loading full-resolution bitmaps.
   304    /// For a 12MP image on a 3x screen, this decodes at ~5.7MP instead of ~48MP bitmap.
   305    private static func downsampledImage(from data: Data, maxPixelSize: Int) -> UIImage? {
   306        let options: [CFString: Any] = [kCGImageSourceShouldCache: false]
   307        guard let source = CGImageSourceCreateWithData(data as CFData, options as CFDictionary) else {
   308            return UIImage(data: data)
   309        }
   310
   311        // Check if downsampling is actually needed
   312        let properties = CGImageSourceCopyPropertiesAtIndex(source, 0, nil) as? [CFString: Any]
   313        let width = properties?[kCGImagePropertyPixelWidth] as? Int ?? 0
   314        let height = properties?[kCGImagePropertyPixelHeight] as? Int ?? 0
   315        let maxDim = max(width, height)
   316
   317        if maxDim <= maxPixelSize {
   318            // Image is already small enough  decode at full resolution
   319            guard let cgImage = CGImageSourceCreateImageAtIndex(source, 0, nil) else {
   320                return UIImage(data: data)
   321            }
   322            return UIImage(cgImage: cgImage)
   323        }
   324
   325        // Downsample to target size
   326        let downsampleOptions: [CFString: Any] = [
   327            kCGImageSourceCreateThumbnailFromImageAlways: true,
   328            kCGImageSourceCreateThumbnailWithTransform: true,
   329            kCGImageSourceThumbnailMaxPixelSize: maxPixelSize,
   330            kCGImageSourceShouldCacheImmediately: true
   331        ]
   332        guard let cgImage = CGImageSourceCreateThumbnailAtIndex(source, 0, downsampleOptions as CFDictionary) else {
   333            return UIImage(data: data)
   334        }
   335        return UIImage(cgImage: cgImage)
   336    }
   337
   338    // MARK: - Actions
   339
   340    private func exportFile() {
   341        guard let file = currentFile, let key = vaultKey else { return }
   342
   343        Task.detached(priority: .userInitiated) {
   344            do {
   345                // Use streaming decryption to avoid 2x memory peak for large files
   346                let (header, tempURL) = try VaultStorage.shared.retrieveFileToTempURL(id: file.id, with: key)
   347                let tempDir = URL(fileURLWithPath: NSTemporaryDirectory(), isDirectory: true)
   348                let ext = (file.filename as NSString?)?.pathExtension ?? header.mimeType.split(separator: "/").last.map(String.init) ?? "bin"
   349                let filename = "Export_\(file.id.uuidString).\(ext)"
   350                let url = tempDir.appendingPathComponent(filename)
   351                try FileManager.default.moveItem(at: tempURL, to: url)
   352                await MainActor.run {
   353                    self.shareURL = url
   354                }
   355            } catch {
   356                // Export failed silently
   357            }
   358        }
   359    }
   360
   361    private func deleteFile() {
   362        guard let file = currentFile, let key = vaultKey else { return }
   363
   364        Task {
   365            do {
   366                try VaultStorage.shared.deleteFile(id: file.id, with: key)
   367            } catch {
   368                EmbraceManager.shared.captureError(error)
   369            }
   370            await MainActor.run {
   371                onDelete?(file.id)
   372                dismiss()
   373            }
   374        }
   375    }
   376}
