   342    /// Performs a complete reset of all app data - DEBUG ONLY
   343    private func debugFullReset() async {
   344        settingsLogger.info("Starting full debug reset")
   345        
   346        // 1. Clear vault files and storage
   347        await clearVaultStorage()
   348        
   349        // 2. Clear recovery phrase mappings
   350        clearRecoveryMappings()
   351        
   352        // 3. Clear all UserDefaults
   353        clearUserDefaults()
   354        
   355        // 4. Clear Keychain entries
   356        clearKeychain()
   357        
   358        // 5. Clear temporary files
   359        clearTemporaryFiles()
   360        
   361        // 6. Reset app state
   362        await MainActor.run {
   363            appState.lockVault()
   364            appState.resetToOnboarding()
   365        }
   366        
   367        settingsLogger.info("Full debug reset complete")
   368    }
