   370    private func clearVaultStorage() async {
   371        // Delete vault storage directory
   372        let fileManager = FileManager.default
   373        let documentsURL = fileManager.urls(for: .documentDirectory, in: .userDomainMask)[0]
   374        let vaultURL = documentsURL.appendingPathComponent("vault_storage")
   375        
   376        try? fileManager.removeItem(at: vaultURL)
   377        
   378        settingsLogger.debug("Vault storage cleared")
   379    }
   380    
   381    private func clearRecoveryMappings() {
   382        UserDefaults.standard.removeObject(forKey: "recovery_mapping")
   383        
   384        settingsLogger.debug("Recovery mappings cleared")
   385    }
   386    
   387    private func clearUserDefaults() {
   388        let domain = Bundle.main.bundleIdentifier!
   389        UserDefaults.standard.removePersistentDomain(forName: domain)
   390        UserDefaults.standard.synchronize()
   391        
   392        settingsLogger.debug("UserDefaults cleared")
   393    }
   394    
   395    private func clearKeychain() {
   396        // Clear all keychain items for the app
   397        let secItemClasses = [
   398            kSecClassGenericPassword,
   399            kSecClassInternetPassword,
   400            kSecClassCertificate,
   401            kSecClassKey,
   402            kSecClassIdentity
   403        ]
   404        
   405        for itemClass in secItemClasses {
   406            let spec: [String: Any] = [kSecClass as String: itemClass]
   407            SecItemDelete(spec as CFDictionary)
   408        }
   409        
   410        settingsLogger.debug("Keychain cleared")
   411    }
   412    
   413    private func clearTemporaryFiles() {
   414        let fileManager = FileManager.default
   415        let tempURL = fileManager.temporaryDirectory
   416        
   417        // Remove all temporary files
   418        if let contents = try? fileManager.contentsOfDirectory(at: tempURL, includingPropertiesForKeys: nil) {
   419            for fileURL in contents {
   420                try? fileManager.removeItem(at: fileURL)
   421            }
   422        }
   423        
   424        settingsLogger.debug("Temporary files cleared")
   425    }
   426    #endif
   427}
