   651    private func handleToggleChange(_ enabled: Bool) {
   652        guard enabled else {
   653            backupTask?.cancel()
   654            backupTask = nil
   655            isBackingUp = false
   656            backupStage = nil
   657            IdleTimerManager.shared.enable()
   658            return
   659        }
   660
   661        // Check iCloud via CloudKit account status before allowing enable
   662        Task { @MainActor in
   663            let available = await checkiCloudAvailable()
   664            guard available else {
   665                isBackupEnabled = false
   666                iCloudAvailable = false
   667                return
   668            }
   669            // First enable  run backup immediately
   670            performBackup()
   671        }
   672    }
