   674    private func performBackup() {
   675        guard !isBackingUp else { return }
   676        guard let key = appState.currentVaultKey else {
   677            errorMessage = "No vault key available"
   678            return
   679        }
   680
   681        isBackingUp = true
   682        backupStage = nil
   683        uploadProgress = 0
   684        errorMessage = nil
   685        IdleTimerManager.shared.disable()
   686
   687        backupTask = Task {
   688            defer {
   689                Task { @MainActor in
   690                    IdleTimerManager.shared.enable()
   691                }
   692            }
   693            do {
   694                try await backupManager.performBackup(with: key.rawBytes, onProgress: { stage in
   695                    Task { @MainActor in
   696                        backupStage = stage
   697                    }
   698                }, onUploadProgress: { progress in
   699                    Task { @MainActor in
   700                        uploadProgress = progress
   701                    }
   702                })
   703                await MainActor.run {
   704                    lastBackupTimestamp = Date().timeIntervalSince1970
   705                    isBackingUp = false
   706                    backupStage = nil
   707                }
   708            } catch is CancellationError {
   709                await MainActor.run {
   710                    isBackingUp = false
   711                    backupStage = nil
   712                }
   713            } catch iCloudError.notAvailable {
   714                await MainActor.run {
   715                    errorMessage = "iCloud is not available. Check that you're signed in to iCloud in Settings."
   716                    isBackingUp = false
   717                    backupStage = nil
   718                }
   719            } catch iCloudError.fileNotFound {
   720                await MainActor.run {
   721                    errorMessage = "No vault data found to back up. Add some files first."
   722                    isBackingUp = false
   723                    backupStage = nil
   724                }
   725            } catch {
   726                EmbraceManager.shared.captureError(
   727                    error,
   728                    context: ["feature": "icloud_backup_settings"]
   729                )
   730                await MainActor.run {
   731                    errorMessage = "\(error.localizedDescription)\n\nError type: \(type(of: error))\nDetails: \(error)"
   732                    isBackingUp = false
   733                    backupStage = nil
   734                }
   735            }
   736        }
   737    }
   738}
   739
   740struct RestoreFromBackupView: View {
   741    @Environment(\.dismiss) private var dismiss
   742    @Environment(AppState.self) private var appState
   743
   744    @State private var patternState = PatternState()
   745    @State private var showFeedback = true
   746    @State private var backupInfo: iCloudBackupManager.BackupMetadata?
   747    @State private var isCheckingBackup = true
   748    @State private var noBackupFound = false
   749    @State private var isRestoring = false
   750    @State private var restoreStage: String?
   751    @State private var errorMessage: String?
   752    @State private var restoreSuccess = false
   753
   754    private let backupManager = iCloudBackupManager.shared
   755
   756    var body: some View {
   757        NavigationStack {
   758            ZStack {
   759                Color.vaultBackground.ignoresSafeArea()
   760
   761                Group {
   762                    if isCheckingBackup {
   763                        ProgressView("Checking for backup...")
   764                    } else if noBackupFound {
   765                        noBackupView
   766                    } else if restoreSuccess {
   767                        successView
   768                    } else {
   769                        restoreContentView
   770                    }
   771                }
   772                .frame(maxWidth: .infinity, maxHeight: .infinity, alignment: .top)
   773            }
   774            .navigationTitle("Restore from Backup")
   775            .navigationBarTitleDisplayMode(.inline)
   776            .toolbarBackground(Color.vaultBackground, for: .navigationBar)
   777            .toolbarBackground(.visible, for: .navigationBar)
   778            .toolbar {
   779                ToolbarItem(placement: .cancellationAction) {
   780                    Button("Cancel") { dismiss() }
   781                }
   782            }
   783        }
   784        .interactiveDismissDisabled(isRestoring)
   785        .task { await checkForBackup() }
   786    }
   787
   788    private var noBackupView: some View {
   789        VStack(spacing: 16) {
   790            Image(systemName: errorMessage != nil ? "exclamationmark.icloud.fill" : "icloud.slash")
   791                .font(.system(size: 48))
   792                .foregroundStyle(errorMessage != nil ? .red : .vaultSecondaryText)
   793
   794            if let errorMessage {
   795                Text("Backup Check Failed")
   796                    .font(.title2).fontWeight(.semibold)
   797                Text(errorMessage)
   798                    .foregroundStyle(.vaultSecondaryText)
   799                    .multilineTextAlignment(.center)
   800                    .padding(.horizontal)
   801
   802                Button("Retry") {
   803                    self.errorMessage = nil
   804                    self.noBackupFound = false
   805                    self.isCheckingBackup = true
   806                    Task { await checkForBackup() }
   807                }
   808                .buttonStyle(.borderedProminent)
   809                .padding(.top, 4)
   810            } else {
   811                Text("No Backup Found")
   812                    .font(.title2).fontWeight(.semibold)
   813                Text("No iCloud backup was found for this account. Enable iCloud Backup and create a backup first.")
   814                    .foregroundStyle(.vaultSecondaryText)
   815                    .multilineTextAlignment(.center)
   816                    .padding(.horizontal)
   817            }
   818        }
   819        .padding(.top, 60)
   820    }
   821
   822    private var successView: some View {
   823        VStack(spacing: 16) {
   824            Image(systemName: "checkmark.circle.fill")
   825                .font(.system(size: 48))
   826                .foregroundStyle(.green)
   827            Text("Restore Complete")
   828                .font(.title2).fontWeight(.semibold)
   829            Text("Your vault has been restored. Lock and re-enter your pattern to access it.")
   830                .foregroundStyle(.vaultSecondaryText)
   831                .multilineTextAlignment(.center)
   832                .padding(.horizontal)
   833
   834            Button("Done") { dismiss() }
   835                .buttonStyle(.borderedProminent)
   836                .padding(.top, 8)
   837        }
   838        .padding(.top, 60)
   839    }
   840
   841    private var restoreContentView: some View {
   842        VStack(spacing: 24) {
   843            // Match pattern-screen scaffold used elsewhere:
   844            // header -> spacer -> 280x280 grid -> spacer -> fixed feedback area.
   845            VStack(spacing: 12) {
   846                if let info = backupInfo {
   847                    HStack(spacing: 12) {
   848                        Image(systemName: "icloud.fill")
   849                            .foregroundStyle(Color.accentColor)
   850                        VStack(alignment: .leading, spacing: 2) {
   851                            Text("Backup from \(info.formattedDate)")
   852                                .font(.subheadline.weight(.medium))
   853                            Text(info.formattedSize)
   854                                .font(.caption)
   855                                .foregroundStyle(.vaultSecondaryText)
   856                        }
   857                        Spacer(minLength: 0)
   858                    }
   859                } else {
   860                    Color.clear.frame(height: 34)
   861                }
   862
   863                Text("Draw your pattern to decrypt the backup")
   864                    .font(.subheadline)
   865                    .foregroundStyle(.vaultSecondaryText)
   866                    .multilineTextAlignment(.center)
   867                    .frame(height: 44, alignment: .top)
   868            }
   869
   870            Spacer(minLength: 0)
   871
   872            if isRestoring {
   873                VStack(spacing: 12) {
   874                    ProgressView()
   875                    Text(restoreStage ?? "Restoring...")
   876                        .font(.subheadline)
   877                        .foregroundStyle(.vaultSecondaryText)
   878                        .multilineTextAlignment(.center)
   879                }
   880                .frame(width: 280, height: 280)
   881                .frame(maxWidth: .infinity)
   882            } else {
   883                PatternGridView(state: patternState, showFeedback: $showFeedback) { pattern in
   884                    performRestore(with: pattern)
   885                }
   886                .frame(width: 280, height: 280)
   887                .frame(maxWidth: .infinity)
   888                .accessibilityIdentifier("restore_pattern_grid")
   889            }
   890
   891            Spacer(minLength: 0)
   892
   893            if let errorMessage {
   894                VStack(spacing: 8) {
   895                    VStack(alignment: .leading, spacing: 4) {
   896                        Label("Restore Failed", systemImage: "exclamationmark.triangle.fill")
   897                            .foregroundStyle(.red)
   898                            .font(.subheadline.weight(.medium))
   899                        Text(errorMessage)
   900                            .foregroundStyle(.vaultSecondaryText)
   901                            .font(.caption)
   902                            .textSelection(.enabled)
   903                    }
   904                    .frame(maxWidth: .infinity, alignment: .leading)
   905
   906                    Button("Try Again") {
   907                        resetForRetry()
   908                    }
   909                    .font(.subheadline)
   910                }
   911            } else {
   912                Color.clear
   913                    .frame(height: 80)
   914            }
   915        }
   916        .padding(.horizontal)
   917        .padding(.top, 8)
   918    }
   919
   920    private func resetForRetry() {
   921        errorMessage = nil
   922        patternState.reset()
   923    }
   924
   925    private func checkForBackup() async {
   926        isCheckingBackup = true
   927        let result = await backupManager.checkForBackup()
   928        await MainActor.run {
   929            switch result {
   930            case .found(let info):
   931                backupInfo = info
   932                noBackupFound = false
   933            case .notFound:
   934                noBackupFound = true
   935            case .error(let message):
   936                noBackupFound = true
   937                errorMessage = message
   938            }
   939            isCheckingBackup = false
   940        }
   941    }
   942
   943    private func performRestore(with pattern: [Int]) {
   944        guard pattern.count >= 6 else {
   945            patternState.reset()
   946            return
   947        }
   948
   949        isRestoring = true
   950        errorMessage = nil
   951        restoreStage = "Deriving encryption key..."
   952
   953        Task {
   954            do {
   955                let key = try await KeyDerivation.deriveKey(from: pattern, gridSize: patternState.gridSize)
   956
   957                await MainActor.run { restoreStage = "Downloading from iCloud..." }
   958                try await backupManager.restoreBackup(with: key)
   959
   960                await MainActor.run {
   961                    isRestoring = false
   962                    restoreSuccess = true
   963                }
   964            } catch iCloudError.notAvailable {
   965                await MainActor.run {
   966                    errorMessage = "iCloud is not available. Check your connection and try again."
   967                    isRestoring = false
   968                    patternState.reset()
   969                }
   970            } catch iCloudError.downloadFailed {
   971                await MainActor.run {
   972                    errorMessage = "Decryption failed. This usually means the pattern doesn't match the one used to create the backup."
   973                    isRestoring = false
   974                    patternState.reset()
   975                }
   976            } catch {
   977                await MainActor.run {
   978                    errorMessage = "\(error.localizedDescription)\n\nDetails: \(error)"
   979                    isRestoring = false
   980                    patternState.reset()
   981                }
   982            }
   983        }
   984    }
   985}
