     3enum OnboardingStep: Int, CaseIterable {
     4    case welcome
     5    case permissions
     6    case analytics
     7    case paywall
     8    case thankYou
     9
    10    var progressFraction: CGFloat {
    11        CGFloat(rawValue + 1) / CGFloat(Self.allCases.count)
    12    }
    13
    14    func next() -> OnboardingStep? {
    15        OnboardingStep(rawValue: rawValue + 1)
    16    }
    17
    18    func previous() -> OnboardingStep? {
    19        OnboardingStep(rawValue: rawValue - 1)
    20    }
    21}
