    23struct OnboardingView: View {
    24    @Environment(AppState.self) private var appState
    25    @Environment(\.accessibilityReduceMotion) private var reduceMotion
    26    @State private var currentStep: OnboardingStep = .welcome
    27    @State private var showPatternSetup = false
    28
    29    var body: some View {
    30        if showPatternSetup {
    31            PatternSetupView(onComplete: { completeOnboarding() })
    32        } else {
    33            VStack(spacing: 0) {
    34                // Progress bar + back arrow
    35                HStack(spacing: 8) {
    36                    Button {
    37                        if let prev = currentStep.previous() {
    38                            withAnimation(animation) { currentStep = prev }
    39                        }
    40                    } label: {
    41                        Image(systemName: "chevron.left")
    42                            .font(.body.weight(.semibold))
    43                            .foregroundStyle(.vaultText)
    44                    }
    45                    .opacity(currentStep != .welcome ? 1 : 0)
    46                    .disabled(currentStep == .welcome)
    47                    .accessibilityIdentifier("onboarding_back")
    48
    49                    Capsule()
    50                        .fill(Color.vaultSecondaryText.opacity(0.2))
    51                        .frame(height: 4)
    52                        .overlay(alignment: .leading) {
