import XCTest
@testable import Vault

final class PerformanceBenchmarkTests: XCTestCase {

    // MARK: - PBKDF2 Key Derivation

    func testPbkdf2600kIterations() async throws {
        let pattern = [0, 1, 2, 5, 8, 7, 6, 3]
        measure {
            let exp = expectation(description: "derive")
            Task {
                _ = try await KeyDerivation.deriveKey(from: pattern, gridSize: 5)
                exp.fulfill()
            }
            wait(for: [exp], timeout: 10)
        }
    }

    // MARK: - Symmetric Encryption/Decryption

    func testEncrypt1mb() throws {
        let key = CryptoEngine.generateRandomBytes(count: 32)!
        let data = CryptoEngine.generateRandomBytes(count: 1_000_000)!
        measure {
            _ = try? CryptoEngine.encrypt(data, with: key)
        }
    }

    func testEncrypt10mb() throws {
        let key = CryptoEngine.generateRandomBytes(count: 32)!
        let data = CryptoEngine.generateRandomBytes(count: 10_000_000)!
        measure {
            _ = try? CryptoEngine.encrypt(data, with: key)
        }
    }

    func testDecrypt1mb() throws {
        let key = CryptoEngine.generateRandomBytes(count: 32)!
        let plaintext = CryptoEngine.generateRandomBytes(count: 1_000_000)!
        let encrypted = try CryptoEngine.encrypt(plaintext, with: key)
        measure {
            _ = try? CryptoEngine.decrypt(encrypted, with: key)
        }
    }

    func testDecrypt10mb() throws {
        let key = CryptoEngine.generateRandomBytes(count: 32)!
        let plaintext = CryptoEngine.generateRandomBytes(count: 10_000_000)!
        let encrypted = try CryptoEngine.encrypt(plaintext, with: key)
        measure {
            _ = try? CryptoEngine.decrypt(encrypted, with: key)
        }
    }

    // MARK: - SecureDelete

    func testSecureDelete1mb() throws {
        measure {
            let tempURL = FileManager.default.temporaryDirectory
                .appendingPathComponent(UUID().uuidString)
            let data = CryptoEngine.generateRandomBytes(count: 1_000_000)!
            try? data.write(to: tempURL)
            try? SecureDelete.deleteFile(at: tempURL)
        }
    }

    func testSecureDelete10mb() throws {
        measure {
            let tempURL = FileManager.default.temporaryDirectory
                .appendingPathComponent(UUID().uuidString)
            let data = CryptoEngine.generateRandomBytes(count: 10_000_000)!
            try? data.write(to: tempURL)
            try? SecureDelete.deleteFile(at: tempURL)
        }
    }

    // MARK: - HMAC

    func testHmac1mb() throws {
        let key = CryptoEngine.generateRandomBytes(count: 32)!
        let data = CryptoEngine.generateRandomBytes(count: 1_000_000)!
        measure {
            _ = CryptoEngine.computeHMAC(for: data, with: key)
        }
    }

    func testHmacVerify1mb() throws {
        let key = CryptoEngine.generateRandomBytes(count: 32)!
        let data = CryptoEngine.generateRandomBytes(count: 1_000_000)!
        let hmac = CryptoEngine.computeHMAC(for: data, with: key)
        measure {
            _ = CryptoEngine.verifyHMAC(hmac, for: data, with: key)
        }
    }
}

import XCTest
@testable import Vault

/// Tests for VaultViewModel covering core functionality.
/// These tests catch common mistakes like:
/// - @MainActor await omission in import handlers
/// - Missing progress updates on MainActor before Task.detached
/// - Not handling all import error cases
@MainActor
final class VaultViewModelTests: XCTestCase {

    private var viewModel: VaultViewModel!
    private var appState: AppState!
    private var subscriptionManager: SubscriptionManager!
    private var testKey: VaultKey!
    private var storage: VaultStorage!

    override func setUp() {
        super.setUp()

        appState = AppState()
        subscriptionManager = SubscriptionManager.shared
        testKey = VaultKey(CryptoEngine.generateRandomBytes(count: 32)!)
        appState.currentVaultKey = testKey
        storage = VaultStorage.shared

        viewModel = VaultViewModel()
        viewModel.configure(appState: appState, subscriptionManager: subscriptionManager)
    }

    override func tearDown() {
        // Clean up test vault
        try? storage.deleteVaultIndex(for: testKey)
        super.tearDown()
    }

    // MARK: - File Operations

    func testLoadFilesPopulatesFilesArray() async throws {
        // Create vault with test files
        let index = try await storage.loadIndex(with: testKey)
        try await storage.saveIndex(index, with: testKey)

        // Add files
        _ = try await storage.storeFile(data: Data("content1".utf8), filename: "file1.txt", mimeType: "text/plain", with: testKey)
        _ = try await storage.storeFile(data: Data("content2".utf8), filename: "file2.txt", mimeType: "text/plain", with: testKey)

        viewModel.loadFiles()
        await viewModel.activeLoadTask?.value

        XCTAssertEqual(viewModel.files.count, 2, "Should load 2 files")
    }

    func testToggleSelectionAddsAndRemoves() async throws {
        // Setup
        let index = try await storage.loadIndex(with: testKey)
        try await storage.saveIndex(index, with: testKey)
        let fileId = try await storage.storeFile(data: Data("content".utf8), filename: "file.txt", mimeType: "text/plain", with: testKey)

        viewModel.loadFiles()
        await viewModel.activeLoadTask?.value
        viewModel.isEditing = true

        // Toggle on
        viewModel.toggleSelection(fileId)
        XCTAssertTrue(viewModel.selectedIds.contains(fileId))

        // Toggle off
        viewModel.toggleSelection(fileId)
        XCTAssertFalse(viewModel.selectedIds.contains(fileId))
    }

    // MARK: - Filter

    func testSetFileFilterUpdatesFilter() {
        viewModel.setFileFilter(.all)
        // Verify filter was set (indirectly via visibleFiles)
        let visible = viewModel.visibleFiles
        XCTAssertNotNil(visible)
    }

    func testSetFileFilterMedia() async throws {
        // Setup with media file
        let index = try await storage.loadIndex(with: testKey)
        try await storage.saveIndex(index, with: testKey)
        _ = try await storage.storeFile(data: Data("image".utf8), filename: "image.jpg", mimeType: "image/jpeg", with: testKey)
        _ = try await storage.storeFile(data: Data("doc".utf8), filename: "doc.pdf", mimeType: "application/pdf", with: testKey)

        viewModel.loadFiles()
        await viewModel.activeLoadTask?.value
        viewModel.setFileFilter(.media)

        let visible = viewModel.visibleFiles
        XCTAssertEqual(visible.media.count, 1, "Media filter should show only 1 media file")
    }

    // MARK: - Import Progress

    func testImportProgressTracksCorrectly() {
        XCTAssertNil(viewModel.importProgress)
        XCTAssertFalse(viewModel.isImporting)

        viewModel.importProgress = (completed: 2, total: 5)
        XCTAssertTrue(viewModel.isImporting)
        XCTAssertEqual(viewModel.importProgress?.completed, 2)
        XCTAssertEqual(viewModel.importProgress?.total, 5)

        viewModel.importProgress = nil
        XCTAssertFalse(viewModel.isImporting)
    }

    // MARK: - Unified Progress Overlay (activeOperationProgress)

    func testActiveOperationProgressNilWhenIdle() {
        // No import, no delete, no transfer  should be nil
        viewModel.importProgress = nil
        viewModel.isDeleteInProgress = false
        XCTAssertNil(viewModel.activeOperationProgress, "Should be nil when no operation is active")
    }

    func testActiveOperationProgressShowsImportProgress() {
        viewModel.importProgress = (completed: 3, total: 10)
        viewModel.isDeleteInProgress = false

        let progress = viewModel.activeOperationProgress
        XCTAssertNotNil(progress)
        XCTAssertEqual(progress?.completed, 3)
        XCTAssertEqual(progress?.total, 10)
        XCTAssertTrue(progress?.message.contains("Importing") == true,
                       "Message should say 'Importing' not 'Deleting'")
        XCTAssertTrue(progress?.message.contains("3") == true)
        XCTAssertTrue(progress?.message.contains("10") == true)
    }

    func testActiveOperationProgressShowsDeleteProgress() {
        viewModel.importProgress = (completed: 5, total: 8)
        viewModel.isDeleteInProgress = true

        let progress = viewModel.activeOperationProgress
        XCTAssertNotNil(progress)
        XCTAssertEqual(progress?.completed, 5)
        XCTAssertEqual(progress?.total, 8)
        XCTAssertTrue(progress?.message.contains("Deleting") == true,
                       "Message should say 'Deleting' when isDeleteInProgress is true")
    }

    func testActiveOperationProgressImportTakesPriorityOverTransfer() {
        // Both import and transfer active  import should win
        viewModel.importProgress = (completed: 1, total: 5)
        viewModel.isDeleteInProgress = false

        let progress = viewModel.activeOperationProgress
        XCTAssertNotNil(progress)
        XCTAssertEqual(progress?.total, 5, "Import progress should take priority over transfer manager")
        XCTAssertTrue(progress?.message.contains("Importing") == true)
    }

    func testActiveOperationProgressZeroProgress() {
        viewModel.importProgress = (completed: 0, total: 10)
        viewModel.isDeleteInProgress = false

        let progress = viewModel.activeOperationProgress
        XCTAssertNotNil(progress)
        XCTAssertEqual(progress?.completed, 0)
        XCTAssertEqual(progress?.total, 10)
    }

    func testActiveOperationProgressCompletedEqualsTotal() {
        viewModel.importProgress = (completed: 10, total: 10)
        viewModel.isDeleteInProgress = false

        let progress = viewModel.activeOperationProgress
        XCTAssertNotNil(progress, "Should still show progress when completed == total (cleanup hasn't run yet)")
        XCTAssertEqual(progress?.completed, 10)
    }

    func testActiveOperationProgressDisappearsWhenImportCleared() {
        viewModel.importProgress = (completed: 3, total: 5)
        XCTAssertNotNil(viewModel.activeOperationProgress)

        viewModel.importProgress = nil
        // transferManager is idle by default
        XCTAssertNil(viewModel.activeOperationProgress, "Should be nil after import progress cleared")
    }

    // MARK: - Shared Vault Update Progress

    func testSharedVaultUpdateProgressNilByDefault() {
        XCTAssertNil(viewModel.sharedVaultUpdateProgress, "Should be nil when no update is running")
    }

    func testActiveOperationProgressShowsSharedVaultUpdateProgress() {
        viewModel.sharedVaultUpdateProgress = (completed: 42, total: 100, message: "Downloading update...")

        let progress = viewModel.activeOperationProgress
        XCTAssertNotNil(progress)
        XCTAssertEqual(progress?.completed, 42)
        XCTAssertEqual(progress?.total, 100)
        XCTAssertEqual(progress?.message, "Downloading update...")
    }

    func testActiveOperationProgressImportTakesPriorityOverSharedVaultUpdate() {
        // Both import and shared vault update active  import should win
        viewModel.importProgress = (completed: 2, total: 5)
        viewModel.isDeleteInProgress = false
        viewModel.sharedVaultUpdateProgress = (completed: 50, total: 100, message: "Downloading update...")

        let progress = viewModel.activeOperationProgress
        XCTAssertNotNil(progress)
        XCTAssertEqual(progress?.completed, 2, "Import progress should take priority over shared vault update")
        XCTAssertEqual(progress?.total, 5)
        XCTAssertTrue(progress?.message.contains("Importing") == true)
    }

    func testActiveOperationProgressSharedVaultUpdateDisappearsWhenCleared() {
        viewModel.sharedVaultUpdateProgress = (completed: 70, total: 100, message: "Importing 3 of 5...")
        XCTAssertNotNil(viewModel.activeOperationProgress)

        viewModel.sharedVaultUpdateProgress = nil
        XCTAssertNil(viewModel.activeOperationProgress, "Should be nil after shared vault update cleared")
    }

    func testActiveOperationProgressSharedVaultUpdateProgressMessages() {
        // Download phase: 070%
        viewModel.sharedVaultUpdateProgress = (completed: 35, total: 100, message: "Downloading update...")
        let downloadProgress = viewModel.activeOperationProgress
        XCTAssertEqual(downloadProgress?.message, "Downloading update...")
        XCTAssertEqual(downloadProgress?.completed, 35)

        // Import phase: 70100%
        viewModel.sharedVaultUpdateProgress = (completed: 85, total: 100, message: "Importing 2 of 4...")
        let importProgress = viewModel.activeOperationProgress
        XCTAssertEqual(importProgress?.message, "Importing 2 of 4...")
        XCTAssertEqual(importProgress?.completed, 85)
    }

    func testHandleVaultKeyChangeClearsSharedVaultUpdateProgress() {
        viewModel.sharedVaultUpdateProgress = (completed: 50, total: 100, message: "Downloading update...")
        viewModel.isUpdating = true

        let newKey = VaultKey(CryptoEngine.generateRandomBytes(count: 32)!)
        viewModel.handleVaultKeyChange(oldKey: testKey, newKey: newKey)

        XCTAssertNil(viewModel.sharedVaultUpdateProgress, "Vault key change should clear shared vault update progress")
        XCTAssertFalse(viewModel.isUpdating, "Vault key change should reset isUpdating")
        XCTAssertNil(viewModel.activeOperationProgress, "Overlay should disappear after key change")
    }

    func testHandleVaultKeyChangeNoOpWhenNoUpdateInProgress() {
        // isUpdating should not be touched when no update is in progress
        viewModel.isUpdating = false
        viewModel.sharedVaultUpdateProgress = nil

        let newKey = VaultKey(CryptoEngine.generateRandomBytes(count: 32)!)
        viewModel.handleVaultKeyChange(oldKey: testKey, newKey: newKey)

        XCTAssertFalse(viewModel.isUpdating, "Should remain false when no update was in progress")
    }

    // MARK: - Free Tier Limits

    func testCanAddFileFreeTierLimit() {
        // Test free tier file limit logic
        // When not premium and at limit
        if !subscriptionManager.isPremium {
            let canAdd = subscriptionManager.canAddFile(currentFileCount: SubscriptionManager.maxFreeFilesPerVault)
            XCTAssertFalse(canAdd, "Should not allow adding files at free tier limit")

            let canAddUnderLimit = subscriptionManager.canAddFile(currentFileCount: SubscriptionManager.maxFreeFilesPerVault - 1)
            XCTAssertTrue(canAddUnderLimit, "Should allow adding files under free tier limit")
        }
    }

    // MARK: - Vault Key Change

    func testHandleVaultKeyChangeClearsFiles() async throws {
        // Setup with files
        let index = try await storage.loadIndex(with: testKey)
        try await storage.saveIndex(index, with: testKey)
        _ = try await storage.storeFile(data: Data("content".utf8), filename: "file.txt", mimeType: "text/plain", with: testKey)

        viewModel.loadFiles()
        await viewModel.activeLoadTask?.value
        XCTAssertEqual(viewModel.files.count, 1)

        // Change key
        let newKey = VaultKey(CryptoEngine.generateRandomBytes(count: 32)!)
        viewModel.handleVaultKeyChange(oldKey: testKey, newKey: newKey)

        // Files should be cleared (will reload for new key)
        XCTAssertEqual(viewModel.files.count, 0, "Files should be cleared on key change")
    }
}

