        XCTAssertGreaterThan(result.metrics.nodeCount, 0)
        XCTAssertGreaterThan(result.metrics.directionChanges, 0)
    }

    // MARK: - Pattern Matching

    func testPatternMatchingSamePatternsMatch() {
