file.content:
.feature-hub-hero {
  padding: 104px 0 36px;
  text-align: center;
}

.feature-hub-hero h1 {
  font-size: clamp(2rem, 4.5vw, 3.2rem);
  margin-bottom: 16px;
}

.feature-hub-hero p {
  color: var(--text-secondary);
  font-size: 1.1rem;
  max-width: 700px;
  margin: 0 auto;
}

.feature-hub-grid {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(320px, 1fr));
  gap: 24px;
  padding: 24px 0 80px;
}

.feature-hub-card {
  background: color-mix(in srgb, var(--surface) 60%, transparent 40%);
  border: 1px solid color-mix(in srgb, var(--text) 10%, transparent 90%);
  padding: 32px;
  display: flex;
  gap: 20px;
  align-items: flex-start;
  clip-path: polygon(16px 0, 100% 0, 100% calc(100% - 16px), calc(100% - 16px) 100%, 0 100%, 0 16px);
  transition: transform 0.2s, border-color 0.2s, box-shadow 0.2s;
  position: relative;
}

.feature-hub-card::after {
  content: '';
  position: absolute;
  top: 0;
  right: 0;
  width: 32px;
  height: 32px;
  background: linear-gradient(to bottom left, var(--highlight) 50%, transparent 50%);
  opacity: 0.2;
}

.feature-hub-card:hover {
  transform: translateY(-4px);
  border-color: var(--accent);
  box-shadow: 0 12px 32px rgba(0, 0, 0, 0.2);
}

.feature-hub-card-icon {
  font-size: 2rem;
  flex-shrink: 0;
}

.feature-hub-card-content h2 {
  font-size: 1.1rem;
  margin-bottom: 8px;
  text-transform: none;
  letter-spacing: 0;
}

.feature-hub-card-content p {
  color: var(--text-secondary);
  font-size: 0.94rem;
  line-height: 1.6;
  margin-bottom: 12px;
}

.feature-hub-card-content .learn-more {
  font-family: var(--font-display);
  font-size: 0.82rem;
  font-weight: 600;
  color: var(--accent);
  text-transform: uppercase;
  letter-spacing: 0.05em;
}

/* â”€â”€ Responsive â”€â”€ */
@media (max-width: 768px) {
  .page-feature .breadcrumbs {
    padding-top: 88px;
  }

  .feature-article {
    padding: 24px 0 60px;
  }

  .feature-hero-icon {
    font-size: 2.8rem;
  }

  .stat-row {
    grid-template-columns: repeat(2, 1fr);
  }

  .flow-diagram {
    flex-direction: column;
    align-items: center;
    gap: 4px;
  }

  .flow-arrow {
    height: auto;
    transform: rotate(90deg);
  }

  .feature-cta {
    padding: 32px 24px;
  }

  .related-grid {
    grid-template-columns: 1fr;
  }

  .feature-hub-grid {
    grid-template-columns: 1fr;
    gap: 16px;
    padding-bottom: 56px;
  }

  .feature-hub-hero {
    padding-top: 96px;
  }

  .feature-hub-card {
    padding: 22px;
    gap: 14px;
  }

  .diagram-container {
    padding: 20px;
    margin: 24px -8px;
  }

  .pattern-grid-diagram {
    gap: 8px;

