file.content:
.features {
  padding: 170px 0 120px;
  background: var(--bg-light);
  color: var(--text-light);
  position: relative;
  margin-top: -50px;
  clip-path: polygon(0 50px, 100% 0, 100% calc(100% - 50px), 0 100%);
}

.section-header {
  margin-bottom: 80px;
  text-align: center;
}

.section-header h2 {
  font-size: 3rem;
  color: var(--text);
  margin-bottom: 16px;
}

.section-header-subtitle {
  color: var(--text-light);
  max-width: 600px;
  margin: 0 auto;
}

.feature-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 30px;
}

.feature-card {
  background: var(--surface-light);
  padding: 40px;
  position: relative;
  clip-path: polygon(
    20px 0, 100% 0,
    100% calc(100% - 20px),
    calc(100% - 20px) 100%,
    0 100%, 0 20px
  );
  transition: transform 0.3s ease;
  box-shadow: 0 20px 40px rgba(32, 35, 60, 0.1);
}

.feature-card:hover {
  transform: translateY(-10px);
}

.feature-card::after {
  content: '';
  position: absolute;
  top: 0; right: 0;
  width: 40px;
  height: 40px;
  background: linear-gradient(to bottom left, var(--highlight) 50%, transparent 50%);
}

.feature-icon {
  font-size: 2rem;
  margin-bottom: 24px;
  color: var(--accent);
}

.feature-card h3 {
  font-size: 1.5rem;
  margin-bottom: 16px;
  color: var(--text);
}

.feature-card p {
  color: var(--text-light);
  font-size: 1rem;
  line-height: 1.7;
}

/* â”€â”€ Timeline â”€â”€ */
.timeline-section {
  padding: 120px 0;
  background: var(--bg);
}

.timeline {
  position: relative;
  max-width: 800px;
  margin: 0 auto;
}

.timeline::before {
  content: '';
  position: absolute;
  left: 50%;
  top: 0;
  bottom: 0;
  width: 2px;
  background: rgba(255,255,255,0.1);
  transform: translateX(-50%);
}

