109‚Üí    func computeVisibleFiles() -> VaultView.VisibleFiles {
110‚Üí        var visible = files
111‚Üí        switch fileFilter {
112‚Üí        case .all:
113‚Üí            break
114‚Üí        case .media:
115‚Üí            visible = visible.filter {
116‚Üí                let mime = $0.mimeType ?? ""
117‚Üí                return mime.hasPrefix("image/") || mime.hasPrefix("video/")
118‚Üí            }
119‚Üí        case .documents:
120‚Üí            visible = visible.filter {
121‚Üí                let mime = $0.mimeType ?? ""
122‚Üí                return !mime.hasPrefix("image/") && !mime.hasPrefix("video/")
123‚Üí            }
124‚Üí        }
125‚Üí        if !searchText.isEmpty {
126‚Üí            visible = visible.filter {
127‚Üí                ($0.filename ?? "").localizedStandardContains(searchText)
128‚Üí            }
129‚Üí        }
130‚Üí        switch sortOrder {
131‚Üí        case .dateNewest:
132‚Üí            visible.sort { ($0.createdAt ?? .distantPast) > ($1.createdAt ?? .distantPast) }
133‚Üí        case .dateOldest:
134‚Üí            visible.sort { ($0.createdAt ?? .distantPast) < ($1.createdAt ?? .distantPast) }
135‚Üí        case .sizeSmallest:
136‚Üí            visible.sort { $0.size < $1.size }
137‚Üí        case .sizeLargest:
138‚Üí            visible.sort { $0.size > $1.size }
139‚Üí        case .name:
140‚Üí            visible.sort { ($0.filename ?? "").localizedStandardCompare($1.filename ?? "") == .orderedAscending }
141‚Üí        }
142‚Üí
143‚Üí        let media = visible.filter { $0.isMedia }
144‚Üí        let documents = visible.filter { !$0.isMedia }
145‚Üí        let mediaIndexById = Dictionary(
146‚Üí            media.enumerated().map { ($1.id, $0) },
147‚Üí            uniquingKeysWith: { first, _ in first }
148‚Üí        )
149‚Üí        return VaultView.VisibleFiles(
150‚Üí            all: visible,
151‚Üí            media: media,
152‚Üí            documents: documents,
153‚Üí            mediaIndexById: mediaIndexById
154‚Üí        )
155‚Üí    }
