174‚Üí    func loadFiles() {
175‚Üí        vmLogger.debug("loadFiles() called (current files.count=\(self.files.count), importActive=\(self.activeImportTask != nil))")
176‚Üí
177‚Üí        guard let key = appState?.currentVaultKey else {
178‚Üí            vmLogger.warning("loadFiles(): no vault key, aborting")
179‚Üí            isLoading = false
180‚Üí            return
181‚Üí        }
182‚Üí
183‚Üí        activeLoadTask?.cancel()
184‚Üí        activeLoadTask = Task.detached(priority: .userInitiated) {
185‚Üí            do {
186‚Üí                let result = try VaultStorage.shared.listFilesLightweight(with: key)
187‚Üí                for entry in result.files {
188‚Üí                    if let encThumb = entry.encryptedThumbnail {
189‚Üí                        await ThumbnailCache.shared.storeEncrypted(id: entry.fileId, data: encThumb)
190‚Üí                    }
191‚Üí                }
192‚Üí                let items = result.files.map { entry in
193‚Üí                    VaultFileItem(
194‚Üí                        id: entry.fileId,
195‚Üí                        size: entry.size,
196‚Üí                        hasThumbnail: entry.encryptedThumbnail != nil,
197‚Üí                        mimeType: entry.mimeType,
198‚Üí                        filename: entry.filename,
199‚Üí                        createdAt: entry.createdAt,
200‚Üí                        duration: entry.duration
201‚Üí                    )
202‚Üí                }
203‚Üí                await MainActor.run {
204‚Üí                    vmLogger.info("loadFiles() loaded \(items.count) files from disk (was \(self.files.count))")
205‚Üí                    self.masterKey = MasterKey(result.masterKey)
206‚Üí                    self.files = items
207‚Üí                    self.isLoading = false
208‚Üí
209‚Üí                    // Auto-detect best filter based on vault contents
210‚Üí                    if !items.isEmpty {
211‚Üí                        let hasNonMedia = items.contains { !$0.isMedia }
212‚Üí                        if !hasNonMedia && self.fileFilter == .all {
213‚Üí                            self.setFileFilter(.media)
214‚Üí                        } else if hasNonMedia && self.fileFilter == .media {
215‚Üí                            self.setFileFilter(.all)
216‚Üí                        }
217‚Üí                    }
218‚Üí
219‚Üí                    EmbraceManager.shared.addBreadcrumb(category: "vault.opened", data: ["fileCount": items.count])
220‚Üí                }
221‚Üí            } catch {
222‚Üí                vmLogger.error("loadFiles() failed: \(error.localizedDescription, privacy: .public)")
223‚Üí                await MainActor.run {
224‚Üí                    // Don't clear files on error - preserves in-memory state after imports
225‚Üí                    // If vault truly failed to load, user would see previous files or empty with error
226‚Üí                    self.isLoading = false
