244‚Üí    func batchDelete() {
245‚Üí        guard let key = appState?.currentVaultKey else { return }
246‚Üí        let idsToDelete = selectedIds
247‚Üí        let count = idsToDelete.count
248‚Üí
249‚Üí        isDeleteInProgress = true
